import { EditorElement } from '@annuadvent/ngx-cms/content-editor';
import { ImageInfo } from '@annuadvent/ngx-cms/cms-image-form';
import { MetaInfo } from '@annuadvent/ngx-common-ui/meta';
import { Category } from '@annuadvent/ngx-cms/category';

export interface Article {
  id?: string; // dash separated article id, that can be used as router path.has to be unique, based on article title.
  metaInfo?: MetaInfo; // This info will be used for SEO for the page., title, description, keywords etc.
  image?: ImageInfo; // Thumbnail Image src
  body?: EditorElement;
  categories?: Array<string>;
  categoriesGroup?: Array<Category>;
  created?: string;
  updated?: string;
  userId?: string; // User Id from Users
  isLive?: boolean; // true if published and live to web.
  inReview?: boolean; // true if the article is up for review, if true, then isLive must be false
  features?: Array<string | ArticleFeatures>;
}

export enum ArticleFeatures {
  featured = 'featured',
  primaryNavigation = 'primary-navigation',
  footerNavigation = 'footer-navigation',
  primeShow = 'prime-show',
  footerShow = 'footer-show',
  primeShowAside = 'prime-show-aside',
  mainShow = 'main-show',
  inReview = 'in-review',
  tnc = 'tnc',
  privacy = 'privacy',
  helpDocs = 'help-docs',
  helpDocsAuthor = 'help-docs-author',
  helpDocsAdmin = 'help-docs-admin',
}

import { OpenaiPrompt } from "@annuadvent/ngx-tools/openai";

export interface ArticlePrompt {
    prompt: OpenaiPrompt;
    heading: string;
};

import { ImageInfo } from '@annuadvent/ngx-cms/cms-image-form';
import { MetaInfo } from '@annuadvent/ngx-common-ui/meta';

export interface Category {
  id?: string;
  shortTitle?: string;
  isFeatured?: boolean; // true, if featured category, this can be shown at primary locations on page.
  metaInfo?: MetaInfo; // This info will be used for SEO for the page., title, description, keywords etc.
  image?: ImageInfo; // Thumbnail Image src
  created?: string;
  updated?: string;
  userId?: string; // User Id from Users
  isLive?: boolean;
  inReview?: boolean; // if category is up for review, if this is true, then isLive must be false;
  features?: Array<CategoryFeatures | string>; // any of the values from CategoryFeature, and any custom values.
}

export enum CategoryFeatures {
  primaryNavigation = 'primary-navigation',
  footerNavigation = 'footer-navigation',
  primeShow = 'prime-show',
  footerShow = 'footer-show',
  primeShowAside = 'prime-show-aside',
  inReview = 'in-review',
  tnc = 'tnc',
  privacy = 'privacy',
  helpDocs = 'help-docs',
  helpDocsAuthor = 'help-docs-author',
  helpDocsAdmin = 'help-docs-admin',
  aboutUs = 'about-us',
  contactUs = 'contact-us',
  vision = 'vision',
}

import { TableInfo } from './table.interface';

export interface EditorElementData {
  src?: string; // Image src
  alt?: string; // Image alt
  text?: string; // html/text content
  source?: string; // Code block
  language?: string; // Code block
  enableEdit?: string; // Code block
  tableData?: TableInfo; // Table Data
}

export interface EditorElement {
  name: string;
  tagName: string;
  isContainer?: boolean;
  focused?: boolean;
  data?: EditorElementData;
  children?: Array<EditorElement>;
  classNames?: Array<string>;
}

