import { Injectable } from '@angular/core';
import { Article } from '@annuadvent/ngx-cms/article';
import { UtilsService } from '@annuadvent/ngx-core/utils';
import { ImageInfo } from '@annuadvent/ngx-common-ui/image-form';
import {
  EditorElement,
  EditorElementData,
} from '@annuadvent/ngx-cms/content-editor';
import { MetaInfo } from '@annuadvent/ngx-common-ui/meta';
import { META_ALLOWED_VALUES } from '@annuadvent/ngx-common-ui/meta';
import { AppConfig, AppConfigService } from '@annuadvent/ngx-core/app-config';
import { OpenaiService, OPENAI_ID_PHRASES } from '@annuadvent/ngx-tools/openai';
import {
  CONTENT_PROMPT_PREFIX,
  DESCRIPTION_PROMPT_PREFIX,
  KEYWORDS_PROMPT_PREFIX,
  MAX_STRONG_FORMATTING_CHAR_COUNT,
  OPENAI_REQUESTS_LIMIT,
  OPENAI_REQUEST_TIME_LIMIT,
} from '../constants/article-editor.constants';
import { Html2JsonService } from '@annuadvent/ngx-cms/content-editor';
import { FireAuthService } from '@annuadvent/ngx-tools/fire-auth';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class ArticleEditorService {
  article$ = new BehaviorSubject<Article>({});

  constructor(
    private utilsService: UtilsService,
    private openaiService: OpenaiService,
    private html2JsonService: Html2JsonService,
    private appConfigService: AppConfigService,
    private fireAuthService: FireAuthService
  ) { }

  public get article(): Observable<Article> {
    return this.article$.asObservable();
  }

  public createInitializedArticle(
    articleTitle: string,
    appConfig: AppConfig
  ): Article {
    return {
      id: this.utilsService.getUniqueFromString(articleTitle),
      metaInfo: {
        title: articleTitle,
        description: '',
        keywords: '',
        robots: META_ALLOWED_VALUES.robots[0].name,
        'Content-Type': META_ALLOWED_VALUES.contentTypes[0].name,
        language: META_ALLOWED_VALUES.languages[0].name,
        'revisit-after': '7 days',
        author: appConfig?.metaInfo?.author,
        type: META_ALLOWED_VALUES.types[0].name,
        image: '',
        url: '', // will be updated post assignment
        card: META_ALLOWED_VALUES.cards[0].name,
        site_name: appConfig?.metaInfo?.title,
        audio: '',
        video: '',
        'article:published_time': this.utilsService.currentDate,
        'article:author':
          appConfig?.metaInfo && appConfig?.metaInfo['article:author'],
        'article:section': META_ALLOWED_VALUES.articleSections[0].name,
        'article:tag': '', // will be updated post assignment
      } as MetaInfo,
      image: {
        src: '',
        alt: articleTitle,
      } as ImageInfo,
      body: {} as EditorElement,
      categories: [],
      created: this.utilsService.currentDate,
      updated: this.utilsService.currentDate,
      userId: this.fireAuthService.getCurrentUserId(),
      isLive: false,
      inReview: true,
      features: [],
    };
  }
  public async generateArticleFromOpenai(
    articleTitle: string,
    appConfig: AppConfig,
    keyWordsPromptPrefix: string,
    descriptionPromptPrefix: string,
    contentPromptPrefix: string
  ): Promise<Article> {
    const timeStarted = Date.now();

    const article: Article = this.createInitializedArticle(
      articleTitle,
      appConfig
    );
    this.article$.next(article);

    // set title prompt content for article body
    article.body = await this.generateArticleContentFromOpenai(
      articleTitle, // article title content.
      contentPromptPrefix
    );
    this.article$.next(article);

    // set keywords
    article.metaInfo.keywords = (
      await this.generateArticleKeywordsFromOpenai(
        articleTitle,
        keyWordsPromptPrefix
      )
    ).join(', ');
    article.metaInfo['article:tag'] = article.metaInfo.keywords;
    this.article$.next(article);

    // set description
    article.metaInfo.description =
      (await this.generateArticleDescriptionFromOpenai(
        articleTitle,
        descriptionPromptPrefix
      )) || '';
    this.article$.next(article);

    // Delay if OPENAI_REQUEST_TIME_LIMIT API time limit is not completed yet.
    const millisecondsEllapsed: number = Date.now() - timeStarted;
    if (millisecondsEllapsed < OPENAI_REQUEST_TIME_LIMIT) {
      await this.utilsService.delay(
        OPENAI_REQUEST_TIME_LIMIT - millisecondsEllapsed
      );
    }

    // set body
    article.body = await this.generateArticleBodyFromOpenai(
      article.metaInfo.keywords.split(', '), // suheading are actually keywords
      contentPromptPrefix
    );
    this.article$.next(article);

    // set url/canonical
    article.metaInfo.url = this.generateArticleUrl(
      article.id,
      article.categories
    );

    // set image
    article.metaInfo.image = article.image.src;

    return article;
  }

  public async generateArticleDescriptionFromOpenai(
    articleTitle: string,
    descriptionPromptPrefix: string
  ): Promise<string> {
    const promptText = `${descriptionPromptPrefix || DESCRIPTION_PROMPT_PREFIX
      }${articleTitle}`;

    const mdText = await this.openaiService.getChatResponse([promptText]);
    const jsonEl = this.html2JsonService.html2json(
      this.html2JsonService.md2html(mdText)
    );

    return this.readDescriptionFromEditorElement(jsonEl);
  }

  public async generateArticleKeywordsFromOpenai(
    articleTitle: string,
    descriptionPromptPrefix: string
  ): Promise<Array<string>> {
    const promptText = `${descriptionPromptPrefix || KEYWORDS_PROMPT_PREFIX
      }${articleTitle}`;

    const mdText = await this.openaiService.getChatResponse([promptText]);
    const jsonEl = this.html2JsonService.html2json(
      this.html2JsonService.md2html(mdText)
    );

    return this.readKeywordsFromEditorElement(jsonEl);
  }

  public async generateArticleContentFromOpenai(
    articleTitle: string,
    contentPromptPrefix: string
  ): Promise<EditorElement> {
    const promptText = `${contentPromptPrefix || CONTENT_PROMPT_PREFIX
      }${articleTitle}`;

    const mdText = await this.openaiService.getChatResponse([promptText]);
    const jsonEl = this.html2JsonService.html2json(
      this.html2JsonService.md2html(mdText)
    );

    return jsonEl;
  }

  public async generateArticleBodyFromOpenai(
    articleSubHeadings: Array<string>,
    contentPromptPrefix: string
  ): Promise<EditorElement> {
    let body: EditorElement = this.utilsService.deepCopy(
      this.article$.value.body
    );

    for (let i = 0; i < articleSubHeadings.length; i += OPENAI_REQUESTS_LIMIT) {
      const timeStarted = Date.now();
      const chunkSubHeadings = articleSubHeadings.slice(
        i,
        i + OPENAI_REQUESTS_LIMIT
      );
      const chunkPromises = chunkSubHeadings.map((promptText) =>
        this.generateArticleContentFromOpenai(promptText, contentPromptPrefix)
      );
      const chunkJsonElms = await Promise.all(chunkPromises);

      chunkJsonElms.forEach((jsonEl, index) => {
        const subHeadingEl: EditorElement = this.generateArticleSubheading(
          articleSubHeadings[i + index]
        );
        body.children = body.children.concat(subHeadingEl, jsonEl.children);
      });

      // Inform article body generation progress of the subheadings
      const tempArticle: Article = this.utilsService.deepCopy(
        this.article$.value
      );
      tempArticle.body = this.cleanAndFormatEditorEl(body, OPENAI_ID_PHRASES);
      this.article$.next(tempArticle);

      // Delay if OPENAI_REQUEST_TIME_LIMIT API time limit is not completed yet.
      const millisecondsEllapsed: number = Date.now() - timeStarted;
      if (
        millisecondsEllapsed < OPENAI_REQUEST_TIME_LIMIT &&
        i + OPENAI_REQUESTS_LIMIT < articleSubHeadings.length
      ) {
        await this.utilsService.delay(
          OPENAI_REQUEST_TIME_LIMIT - millisecondsEllapsed
        );
      }
    }

    return body;
  }

  public generateArticleUrl(
    articleId: string,
    articleCategories: Array<string>
  ): string {
    articleCategories = articleCategories || [];
    const canonicalCategoryId = articleCategories.length
      ? articleCategories[0]
      : '';
    return this.utilsService.getCanonicalUrl(canonicalCategoryId, articleId);
  }

  public readDescriptionFromEditorElement(el: EditorElement): string {
    let str = [];
    if (el.tagName.toLowerCase() === 'p' && el.data?.text) {
      str.push(el.data.text);
    }

    if (el.children && el.children.length) {
      el.children.forEach((chEl) => {
        const chStr = this.readDescriptionFromEditorElement(chEl);
        str.push(chStr);
      });
    }

    return str.join(' ');
  }

  public readKeywordsFromEditorElement(el: EditorElement): Array<string> {
    let str: Array<string> = [];
    if (['ul', 'ol'].includes(el.tagName.toLowerCase())) {
      el.children.forEach((chEl) => {
        if (chEl.tagName.toLowerCase() === 'li' && chEl.data?.text) {
          str.push(chEl.data.text);
        }
      });
    }

    if (el.children && el.children.length) {
      el.children.forEach((chEl) => {
        const chStr = this.readKeywordsFromEditorElement(chEl);
        if (chStr && chStr.length) {
          str = str.concat(chStr);
        }
      });
    }

    return str;
  }

  public generateArticleSubheading(subHeadingText: string): EditorElement {
    const editorEl: EditorElement = {
      name: `h2-${Date.now()}`,
      tagName: 'h2',
      isContainer: false,
      focused: false,
      data: { text: subHeadingText } as EditorElementData,
    };

    return editorEl;
  }

  public generateArticleImageUrl(imageFullPath: string): string {
    return this.utilsService.getImageUrl(
      imageFullPath,
      this.appConfigService.config.imagesSourceUrl
    );
  }

  public cleanAndFormatEditorEl(
    jsonEl: EditorElement,
    phrases: Array<string>
  ): EditorElement {
    return this.formatEditorElement(this.cleanEditorEl(jsonEl, phrases));
  }

  /**
   * Cleans Open ai language keywords and tags with \n only.
   * @date 6/5/2023 - 12:48:30 AM
   *
   * @public
   * @param {EditorElement} jsonEl
   * @param {Array<string>} phrases
   * @returns {EditorElement}
   */
  public cleanEditorEl(
    jsonEl: EditorElement,
    phrases: Array<string>
  ): EditorElement {
    if (!phrases || !phrases.length) return this.utilsService.deepCopy(jsonEl);
    if (!jsonEl) return jsonEl;

    const phrasesF = phrases.filter((phrase) =>
      jsonEl?.data?.text.includes(phrase)
    );
    if (
      phrasesF.length ||
      (jsonEl?.tagName === '#text' && jsonEl?.data?.text === '\n')
    ) {
      return null;
    } else if (jsonEl?.children && jsonEl.children.length) {
      jsonEl.children = jsonEl.children
        .map((el) => this.cleanEditorEl(el, phrases))
        .filter((el) => el !== null);
      return jsonEl;
    }

    return jsonEl;
  }

  /**
   * Formats LI items those having an heading within, as strong.
   * @date 6/5/2023 - 12:55:14 AM
   *
   * @public
   * @param {EditorElement} el
   * @returns {EditorElement}
   */
  public formatEditorElement(el: EditorElement): EditorElement {
    if (['ul', 'ol'].includes(el.tagName.toLowerCase())) {
      el.children.forEach((chEl) => {
        if (chEl.tagName.toLowerCase() === 'li' && chEl.data?.text) {
          const str = chEl.data?.text || '';
          const colonIndex = str.indexOf(':');
          const pIndex = str.indexOf('<p>');
          const pTag =
            pIndex >= 0 && pIndex < MAX_STRONG_FORMATTING_CHAR_COUNT
              ? '<p>'
              : '';
          if (colonIndex > 0 && colonIndex < MAX_STRONG_FORMATTING_CHAR_COUNT) {
            // means is a bold text
            let strongText = str.substring(0, colonIndex);
            const normalText = str.substring(colonIndex);
            strongText = pTag ? strongText.replace('<p>', '') : strongText;
            chEl.data.text = `${pTag}<strong>${strongText}</strong>${normalText}`;
          }
        }
      });
    }

    if (el.children && el.children.length) {
      el.children = el.children.map((chEl) => this.formatEditorElement(chEl));
    }

    return el;
  }
}

import { Injectable } from '@angular/core';
import { EditorElement, EditorElementData } from '../interfaces/content-editor.interface';
import { ImageInfo } from '@annuadvent/ngx-common-ui/image-form';
import { SUPPORTED_TAGS } from '../constants/content-editor.constants';

@Injectable({
  providedIn: 'root'
})
export class ContentEditorService {

  constructor() { }

  private getEditorElementName(elType: string): string {
    return `${elType}-${Date.now()}`;
  }

  public findParent(el: EditorElement, parent: EditorElement): EditorElement {
    if (!parent.isContainer || !parent.children || !parent.children.length) {
      return null;
    }

    let foundParent: EditorElement;

    for (let i = 0; i < parent.children.length; i++) {
      const chEl = parent.children[i];

      if (el.name === chEl.name) {
        foundParent = parent;
        break;
      }

      foundParent = this.findParent(el, chEl);
      if (foundParent) {
        break;
      }
    }

    return foundParent;
  }

  public addNewEditorElement(el: EditorElement, fullTree: EditorElement) {
    const oldEl = { ...el };
    const parent = this.findParent(el, fullTree);
    const index = parent.children.indexOf(el);
    //Remove focus from all other elements
    parent.children.forEach(ch => ch.focused = false);

    //Add element with focus
    parent.children.splice(index + 1, 0, {
      tagName: oldEl.tagName,
      name: this.getEditorElementName(oldEl.tagName),
      focused: true,
      data: {
        src: '',
        url: '',
        text: '',
        alt: '',
        source: SUPPORTED_TAGS.CODE_BLOCK === oldEl.tagName ? '<h1>Sample source code</h1>' : '',
        language: SUPPORTED_TAGS.CODE_BLOCK === oldEl.tagName ? 'markup' : '',
      } as EditorElementData
    } as EditorElement);
  }

  public removeEditorElement(el: EditorElement, fullTree: EditorElement) {
    // if the element to remove is the only child of root, then do not remove
    if (fullTree.children.length === 1) {
      return;
    }

    // Find the parent of selected Element
    const parent = this.findParent(el, fullTree);
    if (!parent) {
      console.error('No Parent found or Element is Root element itself.');
      return;
    }
    const index = parent.children.indexOf(el);
    // remove selected child
    parent.children.splice(index, 1);

    if (parent.children.length) {
      const nextOrPreviousItemIndex = index >= parent.children.length ? index - 1 : index;
      const childToFocus = parent.children[nextOrPreviousItemIndex];
      if (childToFocus.isContainer && childToFocus.children && childToFocus.children.length) {
        childToFocus.children[childToFocus.children.length - 1].focused = true;
      } else {
        childToFocus.focused = true;
      }
    } else {
      this.removeEditorElement(parent, fullTree);
    }
  }

  public setFocusOffAll(fullTree: EditorElement) {
    if (fullTree.focused) {
      fullTree.focused = false;
    }
    if (fullTree.children && fullTree.children.length) {
      fullTree.children.forEach(child => this.setFocusOffAll(child))
    }
  }

  public replaceElement(el: EditorElement, tagName: string, fullTree: EditorElement, data: ImageInfo | any = null) {

    if (el.tagName === SUPPORTED_TAGS.LIST_ITEM) {
      const parent = this.findParent(el, fullTree);
      if (['ol', 'ul'].includes(tagName)) {
        if (parent.tagName !== tagName) {
          parent.name = this.getEditorElementName(tagName);
          parent.tagName = tagName;
        }
      } else {
        // remove LI and add copy as new item below the parent List (ul/ol)
        const newItem = { ...el };
        newItem.tagName = tagName;
        newItem.name = this.getEditorElementName(tagName);
        if (tagName === SUPPORTED_TAGS.IMAGE && data) {
          newItem.data.src = data.src;
          newItem.data.alt = data.alt;
          delete newItem.data.text;
        }

        const parentOfParent = this.findParent(parent, fullTree);
        let indexOfParent = parentOfParent.children.indexOf(parent);
        if (parent.children.length > 1) {
          indexOfParent++;
        }
        this.removeEditorElement(el, fullTree);
        this.setFocusOffAll(fullTree);
        parentOfParent.children.splice(indexOfParent, 0, newItem);
      }
    } else {
      if (['ol', 'ul'].includes(tagName)) {
        const newListItem = { ...el };
        newListItem.tagName = SUPPORTED_TAGS.LIST_ITEM;
        newListItem.name = this.getEditorElementName(SUPPORTED_TAGS.LIST_ITEM);
        if (el.tagName === SUPPORTED_TAGS.IMAGE) {
          newListItem.data.text = newListItem.data.alt;
          delete newListItem.data.alt;
          delete newListItem.data.src;
        }

        el.tagName = tagName;
        el.name = this.getEditorElementName(tagName);
        el.isContainer = true;
        el.children = [newListItem];
        delete el.data;
        delete el.focused;
      } else {
        if (tagName === SUPPORTED_TAGS.IMAGE && data) {
          el.data.src = data.src;
          el.data.alt = data.alt;
          delete el.data.text;
        } else if (tagName === SUPPORTED_TAGS.CODE_BLOCK && data) {
          el.data.source = data.source;
          el.data.language = data.language;
        }

        el.tagName = tagName;
        el.name = this.getEditorElementName(tagName);
      }
    }


    /*
      if source LI
        if  Bullet/Numbering
          find parent and change type to ol/ul
        else
          hide toolbar icons => p/H1-H6
      else
        if (target == ul/ol)
          change name
          isContainer = true
          add one child LI and focus
        else
          change name as is
    */
  }
}

import { Inject, Injectable } from '@angular/core';
import { EditorElement, EditorElementData } from '../interfaces/content-editor.interface';
import { DOCUMENT } from '@angular/common';
import * as showdown from 'showdown';

@Injectable({
  providedIn: 'root',
})
export class Html2JsonService {
  constructor(@Inject(DOCUMENT) private document: Document) { }

  public md2html(
    mdText: string,
    replaceNewlineWith: string = '<p></p>'
  ): string {
    const converter = new showdown.Converter();
    let html = converter.makeHtml(mdText);
    // html = html.replaceAll('\\n', replaceNewlineWith);

    return html;
  }

  public html2json(htmlText: string): EditorElement {
    const rootEl = this.document.createElement('article');
    rootEl.innerHTML = htmlText;
    rootEl.id = 'root';
    const htmlJson: EditorElement = this.parseHtmlElement(rootEl);

    return htmlJson;
  }

  private parseHtmlElement(el: HTMLElement | ChildNode): EditorElement {
    const tagName = (
      (el as HTMLElement).tagName || (el as ChildNode).nodeName
    ).toLowerCase();
    const name = (el as HTMLElement).id || `${tagName}-${Date.now()}`;
    const isContainer = ['ol', 'ul', 'article'].includes(tagName);
    const focused = false;
    const editorEl: EditorElement = { name, tagName, isContainer, focused };

    if (el.childNodes && el.childNodes.length) {
      const elementChildren = el.childNodes;
      const children: Array<EditorElement> = [];
      if (tagName === 'pre') {
        let codeEl;
        el.childNodes.forEach((node) => {
          if (node.nodeName === 'CODE') {
            codeEl = node;
          }
        });

        return {
          ...editorEl,
          tagName: 'anu-code-block',
          data: this.getContent(codeEl),
        };
      } else if (
        ['p', 'li', 'h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(tagName)
      ) {
        return {
          ...editorEl,
          data: {
            text:
              tagName === 'p'
                ? (el as HTMLElement).innerHTML
                : (el as HTMLElement).textContent,
          },
        };
      } else {
        for (let i = 0; i < elementChildren.length; i++) {
          const elObj = this.parseHtmlElement(elementChildren[i]);
          children.push(elObj);
        }

        const data = tagName === 'a' ? this.getContent(el) : '';

        if (data) {
          return { ...editorEl, data, children };
        } else {
          return { ...editorEl, children };
        }
      }
    }

    return { ...editorEl, data: this.getContent(el) };
  }

  private getContent(el: ChildNode): EditorElementData {
    const content: EditorElementData = {};
    const tagName = (el as HTMLElement).tagName || (el as ChildNode).nodeName;

    switch (tagName) {
      case 'IMG':
        const imgEl = el as HTMLImageElement;
        content.src = imgEl.src || '';
        content.alt = imgEl.alt || '';
        break;
      case 'A':
        const aEl = el as HTMLAnchorElement;
        content.href = aEl.href || '';
        break;
      case 'CODE':
        const codeEl = el as HTMLElement;
        content.source = codeEl.innerHTML || '';
        content.language = codeEl.lang || '';
        break;
      case '#text':
        const textEl = el as HTMLSpanElement;
        content.text = textEl.textContent || '';
        break;
      default:
        content.text = el.textContent || '';
    }

    return content;
  }
}

import { Inject, Injectable } from '@angular/core';
import { Link } from '@annuadvent/ngx-common-ui/link-form';
import { ImageInfo } from '@annuadvent/ngx-common-ui/image-form';
import { Observable, Subject } from 'rxjs';
import { DOCUMENT } from '@angular/common';
import { Rectangle } from '../interfaces/content-editor.interface';
import { SUPPORTED_TAGS } from '../constants/content-editor.constants';

@Injectable({
  providedIn: 'root'
})
export class SelectionService {
  private savedSelection: Array<Range>;
  private _selection = new Subject<boolean>();

  constructor(@Inject(DOCUMENT) private document: Document) { }

  // Get the deepest Element node in the DOM tree that contains the entire range.
  private getRangeContainer(range: Range): Node {
    let container = range.commonAncestorContainer;
    // If the selected node is a Text node, climb up to an element node - in Internet
    // Explorer, the .contains() method only works with Element nodes.
    while (container && container.nodeName !== 'ANU-CONTENT-EDITOR') {
      container = container.parentNode;
    }

    return container;
  }

  private restoreSelection(savedSel: Array<Range>): Selection {
    if (savedSel) {
      const selection: Selection = this.document.getSelection();
      selection.removeAllRanges();
      for (var i = 0, len = savedSel.length; i < len; ++i) {
        selection.addRange(savedSel[i]);
      }
      return selection;
    }

    return null;
  }

  private isImageInSelection(selection: Selection) {
    if (!selection || !selection.rangeCount) return false;

    const range = selection.getRangeAt(0);

    // check if selection contains an image, if yes then Image should also be the hyperlink.
    const fragment = range.cloneContents();
    const imgs = fragment.querySelectorAll(SUPPORTED_TAGS.IMAGE);

    if (imgs && imgs.length) return true

    return false;
  }

  public get selection(): Observable<boolean> {
    return this._selection.asObservable();
  }

  public get selectionText(): string {
    this.restoreSelection(this.savedSelection);
    return this.document.getSelection().toString();
  }

  // You can save selection because when you focus on some other element, current selection gets lost,
  // so you can save it and restore it for processing.
  public saveSelection(selection: Selection) {
    var ranges: Array<Range> = [];
    for (var i = 0, len = selection.rangeCount; i < len; ++i) {
      ranges.push(selection.getRangeAt(i));
    }
    this.savedSelection = ranges;

    if (selection.toString() || this.isImageInSelection(selection)) {
      this._selection.next(true);
    } else {
      this._selection.next(false);
    }
  }

  public getSelectionRect(): Rectangle | null {
    const selection = this.restoreSelection(this.savedSelection);
    if (!selection || selection.type !== 'Range' || !selection.rangeCount) {
      return null;
    }

    // // save selection for later use
    // this.saveSelection(selection);

    // Calculate the selection bounding rectangle
    const range = selection.getRangeAt(0);
    const rangeContainer = this.getRangeContainer(range) as Element;
    if (!rangeContainer) {
      return null;
    }
    const rangeRect = range.getBoundingClientRect();
    const rangeContainerRect = rangeContainer.getBoundingClientRect();
    const selectionRect: Rectangle = {
      left: rangeRect.left - rangeContainerRect.left,
      top: rangeRect.top - rangeContainerRect.top,
      bottom: rangeRect.top - rangeContainerRect.top + rangeRect.height,
      right: rangeRect.left - rangeContainerRect.left + rangeRect.width,
      width: rangeRect.width,
      height: rangeRect.height,
    };

    return selectionRect;
  }


  public addLink(link: Link) {
    const selection = this.restoreSelection(this.savedSelection);
    if (!selection || selection.type !== 'Range' || !selection.rangeCount) {
      return;
    }

    const selectionText = selection.toString();
    if (!selectionText && !this.isImageInSelection(selection)) {
      return;
    }

    const range = selection.getRangeAt(0);

    // check if selection contains an image, if yes then Image should also be the hyperlink.
    const fragment = range.cloneContents();
    const imgs = fragment.querySelectorAll(SUPPORTED_TAGS.IMAGE);
    let linkContentNode: any = this.document.createTextNode(link.label);
    if (imgs && imgs.length) {
      linkContentNode = fragment;
    }

    // create a new Link
    var newLink = this.document.createElement("a");
    newLink.target = link.target;
    newLink.href = link.href;
    newLink.title = link.title;
    newLink.appendChild(linkContentNode);

    // replace selection with Link
    range.deleteContents();
    range.insertNode(newLink);
  }

  public addImage(image: ImageInfo) {
    const selection = this.restoreSelection(this.savedSelection);
    if (!selection || selection.type !== 'Range' || !selection.rangeCount) {
      return;
    }

    const selectionText = selection.toString();
    if (!selectionText && !this.isImageInSelection(selection)) {
      return;
    }

    const range = selection.getRangeAt(0);

    // create a new Image
    var newImage = this.document.createElement(SUPPORTED_TAGS.IMAGE);
    newImage.src = image.src;
    newImage.alt = image.alt;
    // newImage.appendChild(newImage);

    // replace selection with Image
    range.deleteContents();
    range.insertNode(newImage);
  }

  public addFormating(tagName: string) {
    const selection = this.restoreSelection(this.savedSelection);
    if (!selection || selection.type !== 'Range' || !selection.rangeCount) {
      return;
    }

    const selectionText = selection.toString();
    if (!selectionText) {
      return;
    }

    const range = selection.getRangeAt(0);

    // create a new formatting Element
    var newEl = this.document.createElement(tagName);
    var textNode = this.document.createTextNode(selectionText);
    newEl.appendChild(textNode);

    // replace selection with new formatting element
    range.deleteContents();
    range.insertNode(newEl);
  }
}

