import { Component, HostBinding, Input, OnInit } from '@angular/core';

/**
 * CSS classes based drawer component. Show the drawer using below CSS classes:</br>
 * <table><tr><th>CSS Class Name</th><th>Description</th></tr>
 * <tr><td><code>.drawer-content-pushed</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>pushes surrounding content, when visible.</td></tr>
 * <tr><td><code>.drawer-relative</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>overlays over its relative parent, when visible.</td></tr>
 * <tr><td><code>.drawer-window</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>overlays over browser window, when visible.</td></tr>
 * <tr><td><code>.drawer-top</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>Shows at top of its relative parent or window.</td></tr>
 * <tr><td><code>.drawer-right</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>Shows at right of its relative parent or window.</td></tr>
 * <tr><td><code>.drawer-bottom</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>Shows at bottom of its relative parent or window.</td></tr>
 * <tr><td><code>.drawer-left</code></br><small>Postfixes: -sm/-md/-lg.</small></td><td>Shows at left of its relative parent or window.</td></tr>
 * </table>
 * @date 7/3/2022 - 5:16:02 pm
 *
 * @export
 * @class DrawerComponent
 * @typedef {DrawerComponent}
 * @implements {OnInit}
 */
@Component({
  selector: 'anu-drawer',
  templateUrl: './drawer.component.html',
  styleUrls: ['./drawer.component.scss']
})
export class DrawerComponent implements OnInit {
  /**
   * Property opened shows or hides the drawer based on value true/false;
   */
  @Input() opened: boolean = false;

  /**
   * position shows the drawer top/right/bottom/left from the relative parent or from the screen based on drawer overlay class names.
   */
  @Input() positionClassNames: Array<string> = ['drawer-left'];

  /**
   * Shows drawer relative to parent, window, push content.
   */
  @Input() overlayClassNames: Array<string> = ['drawer-content-pushed'];


  @HostBinding('class.drawer-opened') get isOpenMethod() { return this.opened; }
  @HostBinding('class') get isPositionMethod() {
    return [...this.positionClassNames, ...this.overlayClassNames];
  }


  constructor() {}

  ngOnInit(): void {

  }

}

styleUrlsData.data:
@import 'mixins';

$max-transition-size: none;

@mixin mx-drawer-content-pushed {
    position: static;
}

@mixin mx-drawer-relative {
    position: absolute;
    z-index: #{$aside-nav-z-index};
}

@mixin mx-drawer-window {
    position: fixed;
    max-width: 100%;
    max-height: 100%;
    z-index: #{$aside-nav-z-index};
    overflow: auto;
}

@mixin mx-drawer-top {
    top: 0;
    left: 0;
    transform-origin: top center;
    max-height: 0;
    transform: scaleY(0);
}

@mixin mx-drawer-right {
    top: 0;
    right: 0;
    transform-origin: center right;
    max-width: 0;
    transform: scaleX(0);
}

@mixin mx-drawer-bottom {
    bottom: 0;
    left: 0;
    transform-origin: bottom center;
    max-height: 0;
    transform: scaleY(0);
}

@mixin mx-drawer-left {
    top: 0;
    left: 0;
    transform-origin: center left;
    max-width: 0;
    width: 0;
    transform: scaleX(0);
    padding: 0;
}

@mixin mx-window-opened-max-size {
    &.drawer {
        &-opened {
            &.drawer-top {
                max-height: 100%;

                &-sm,
                &-md,
                &-lg {
                    max-height: 100%;
                }
            }

            &.drawer-right {
                max-width: 100%;

                &-sm,
                &-md,
                &-lg {
                    max-width: 100%;
                }
            }

            &.drawer-bottom {
                max-height: 100%;

                &-sm,
                &-md,
                &-lg {
                    max-height: 100%;
                }
            }

            &.drawer-left {
                max-width: 100%;

                &-sm,
                &-md,
                &-lg {
                    max-width: 100%;
                }
            }
        }
    }
}

:host {
    display: flex;
    flex-direction: column;
    position: static;
    transition: all 0.5s;
    transition-property: max-height, max-width, transform, opacity;
    opacity: 0;

    &.drawer {
        &-opened {
            opacity: 1;

            &.drawer-top {
                max-height: #{$max-transition-size};
                transform: scaleY(1);

                &-sm,
                &-md,
                &-lg {
                    max-height: #{$max-transition-size};
                    transform: scaleY(1);
                }

            }

            &.drawer-right {
                max-width: #{$max-transition-size};
                transform: scaleX(1);

                &-sm,
                &-md,
                &-lg {
                    max-width: #{$max-transition-size};
                    transform: scaleX(1);
                }

            }

            &.drawer-bottom {
                max-height: #{$max-transition-size};
                transform: scaleY(1);

                &-sm,
                &-md,
                &-lg {
                    max-height: #{$max-transition-size};
                    transform: scaleY(1);
                }

            }

            &.drawer-left {
                max-width: #{$max-transition-size};
                transform: scaleX(1);
                width: initial;

                &-sm,
                &-md,
                &-lg {
                    max-width: #{$max-transition-size};
                    transform: scaleX(1);
                    width: initial;
                }

            }
        }

        &-content-pushed {
            @include mx-drawer-content-pushed;

            &-sm {
                @include small {
                    @include mx-drawer-content-pushed;
                }
            }

            &-md {
                @include medium {
                    @include mx-drawer-content-pushed;
                }
            }

            &-lg {
                @include large {
                    @include mx-drawer-content-pushed;
                }
            }
        }

        &-relative {
            @include mx-drawer-relative;

            &-sm {
                @include small {
                    @include mx-drawer-relative;
                }
            }

            &-md {
                @include medium {
                    @include mx-drawer-relative;
                }
            }

            &-lg {
                @include large {
                    @include mx-drawer-relative;
                }
            }
        }

        &-window {
            @include mx-drawer-window;
            @include mx-window-opened-max-size;

            &-sm {
                @include small {
                    @include mx-drawer-window;
                    @include mx-window-opened-max-size;
                }
            }

            &-md {
                @include medium {
                    @include mx-drawer-window;
                    @include mx-window-opened-max-size;
                }
            }

            &-lg {
                @include large {
                    @include mx-drawer-window;
                    @include mx-window-opened-max-size;
                }
            }
        }

        &-top {
            @include mx-drawer-top;

            &-sm {
                @include small {
                    @include mx-drawer-top;
                }
            }

            &-md {
                @include medium {
                    @include mx-drawer-top;
                }
            }

            &-lg {
                @include large {
                    @include mx-drawer-top;
                }
            }
        }

        &-right {
            @include mx-drawer-right;

            &-sm {
                @include small {
                    @include mx-drawer-right;
                }
            }

            &-md {
                @include medium {
                    @include mx-drawer-right;
                }
            }

            &-lg {
                @include large {
                    @include mx-drawer-right;
                }
            }
        }

        &-bottom {
            @include mx-drawer-bottom;

            &-sm {
                @include small {
                    @include mx-drawer-bottom;
                }
            }

            &-md {
                @include medium {
                    @include mx-drawer-bottom;
                }
            }

            &-lg {
                @include large {
                    @include mx-drawer-bottom;
                }
            }
        }

