import { HttpClient } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { Address } from '@annuadvent/ngx-core/helpers-ecommerce';
import { BehaviorSubject, Observable, lastValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AddressService {
  private $list = new BehaviorSubject<Array<Address>>([]);
  private $defaultAddress = new BehaviorSubject<Address>(null);

  constructor(
    private http: HttpClient,
    @Inject('API_URLS') private API_URLS: any
  ) {}

  public get list(): Observable<Array<Address>> {
    return this.$list.asObservable();
  }

  public get defaultAddress(): Observable<Address> {
    return this.$defaultAddress.asObservable();
  }

  public set defaultAddress(v: string | Address) {
    if (v instanceof Object) {
      this.$defaultAddress.next(
        this.$list.value.find((adr) => adr.id === v.id) || null
      );
    } else if (typeof v === 'string') {
      this.$defaultAddress.next(
        this.$list.value.find((adr) => adr.id === v) || null
      );
    } else {
      this.$defaultAddress.next(null);
    }
  }

  public async getList(): Promise<Array<Address>> {
    // List from cache
    if (this.$list.value && this.$list.value.length) {
      return this.$list.value;
    }

    // list from database
    try {
      const list: any = await lastValueFrom(
        this.http.get(this.API_URLS.ADDRESS.LIST)
      );

      const addresses = list.map((address) => new Address(address));
      this.$list.next(addresses);
      return addresses;
    } catch (error: any) {
      this.$list.next([]);
      throw error;
    }
  }

  public async addAddress(address: Address): Promise<Address> {
    try {
      const result: any = await lastValueFrom(
        this.http.post(`${this.API_URLS.ADDRESS.ADD}`, address)
      );
      const addressR = new Address(result);
      const newList = [addressR, ...this.$list.value];
      this.$list.next(newList);
      return addressR;
    } catch (error: any) {
      throw error;
    }
  }

  public async updateAddress(address: Address): Promise<Address> {
    try {
      const result: any = await lastValueFrom(
        this.http.post(`${this.API_URLS.ADDRESS.UPDATE}/${address.id}`, address)
      );
      const addressR = new Address(result);
      const newList = this.$list.value.map((adr) =>
        adr.id === addressR.id ? addressR : adr
      );
      this.$list.next(newList);
      return addressR;
    } catch (error: any) {
      throw error;
    }
  }

  public async deleteAddress(id: string): Promise<boolean> {
    try {
      const result: any = await lastValueFrom(
        this.http.post(`${this.API_URLS.ADDRESS.DELETE}/${id}`, {})
      );
      const newList = this.$list.value.filter((adr) => adr.id !== id);
      this.$list.next(newList);

      // Check if deleted address was the default address too. Reset Default address
      if (this.$defaultAddress.value?.id === id) {
        this.$defaultAddress.next(null);
      }

      return result.success;
    } catch (error: any) {
      throw error;
    }
  }
}

import { Injectable } from '@angular/core';
import { ImageUpload } from '@annuadvent/ngx-common-ui/image-upload';
import {
  GlobalConfigParamsEnum,
  GlobalConfigService
} from '@annuadvent/ngx-core/global-config';
import { FireStorageImageService } from '@annuadvent/ngx-tools/fire-storage';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AppImagesService {
  private $images = new BehaviorSubject<Array<string>>([]);
  private $loading = new BehaviorSubject<boolean>(false);

  public get images(): Observable<Array<string>> {
    return this.$images.asObservable();
  }

  public get loading(): Observable<boolean> {
    return this.$loading.asObservable();
  }

  constructor(
    private gcSrvice: GlobalConfigService,
    private fireImageService: FireStorageImageService
  ) {}

  public async getImages(): Promise<Array<string>> {
    this.$loading.next(true);
    try {
      const fullPath = this.gcSrvice.getValue(
        GlobalConfigParamsEnum.appImagePath
      );
      const imageList = await this.fireImageService.getImagesListByPath(
        fullPath
      );
      const images = imageList.imageFiles.map((f) => f.name);
      this.$images.next(images);
      this.$loading.next(false);
      return images;
    } catch (error) {
      this.$loading.next(false);
      this.$images.next([]);
      throw error;
    }
  }

  public async uploadImages(imageUploads: Array<ImageUpload>): Promise<string> {
    this.$loading.next(true);
    let rStr = '';
    const successImgs = [];
    try {
      const rootPath = this.gcSrvice.getValue(
        GlobalConfigParamsEnum.appImagePath
      );
      const imgPromises = imageUploads.map((imgU) => {
        const fullPath = `${rootPath}/${imgU.fileName}`;
        return this.fireImageService.uploadImageByPath(
          fullPath,
          imgU.data,
          false
        );
      });

      const result = await Promise.allSettled(imgPromises);

      result.map((res, index) => {
        if (res.status === 'rejected') {
          rStr += `Failed Upload [${imageUploads[index].fileName}] - ${res.reason}`;
        } else {
          successImgs.push(imageUploads[index].fileName);
        }
      });

      this.$images.next([...successImgs, ...this.$images.value]);
      this.$loading.next(false);
      return rStr;
    } catch (error) {
      this.$loading.next(false);
      throw error;
    }
  }

  public async deleteImage(img: string): Promise<void> {
    this.$loading.next(true);
    try {
      const fullPath = `${this.gcSrvice.getValue(
        GlobalConfigParamsEnum.appImagePath
      )}/${img}`;

      const result = await this.fireImageService.deleteImageByPath(fullPath);
      const images = this.$images.value.filter((im) => im !== img);
      this.$images.next(images);
      this.$loading.next(false);
      return;
    } catch (err) {
      this.$loading.next(false);
      throw err;
    }
  }
}

import { HttpClient } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import {
  Cart,
  CartProducts,
  InCartProduct,
  Product,
  ProductQueryService
} from '@annuadvent/ngx-core/helpers-ecommerce';
import { FireAuthService } from '@annuadvent/ngx-tools/fire-auth';
import { BehaviorSubject, Observable, lastValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private $cart = new BehaviorSubject<Cart>(null);
  private $cartProducts = new BehaviorSubject<CartProducts>({});

  public get cart(): Observable<Cart> {
    return this.$cart.asObservable();
  }

  public get cartProducts(): Observable<CartProducts> {
    return this.$cartProducts.asObservable();
  }

  constructor(
    private authService: FireAuthService,
    private http: HttpClient,
    private pqS: ProductQueryService,
    @Inject('API_URLS') private API_URLS: any
  ) {
    // Get the Cart from localstorage if exists or from database
    if (typeof localStorage !== 'undefined') {
      const cartStr = localStorage.getItem('cart');
      if (cartStr) {
        const cart: Cart = JSON.parse(cartStr);
        this.$cart.next({ ...cart });
      } else {
        this.getCartFromDb()
          .then((cart) => {
            this.$cart.next(cart);
          })
          .catch((err) => {
            this.$cart.next(null);
          });
      }
    }

    // Subscribe to cart, and fetch cart products
    this.cart.subscribe((cart) => {
      const pIds = cart?.products?.map((p) => p.id) || [];
      if (pIds.length) {
        this.pqS
          .getProductsByIds(pIds)
          .then((ps) => {
            const cPs = {};
            ps.forEach((p) => (cPs[p.id] = p));
            this.$cartProducts.next(cPs);
          })
          .catch((err) => this.$cartProducts.next({}));
      } else {
        this.$cartProducts.next({});
      }
    });
  }

  private updateCart(cart: Cart): void {
    cart.updateTime = new Date().toDateString();

    // Emit cart value
    this.$cart.next({ ...cart });

    // Update local storage
    localStorage.setItem('cart', JSON.stringify(cart));

    // Update database siliently
    this.updateOrAddCartInDb(cart);
  }

  private async updateOrAddCartInDb(cart: Cart): Promise<void> {
    const url = `${this.API_URLS.CART.ADD}`;

    try {
      const result = await lastValueFrom(await this.http.post(url, cart));
      return;
    } catch (error) {
      return;
    }
  }

  private async deleteCartInDb(cart: Cart): Promise<void> {
    const url = `${this.API_URLS.CART.DELETE}`;

    try {
      const result = await lastValueFrom(await this.http.post(url, cart));
      return;
    } catch (error) {
      return;
    }
  }

  private async getCartFromDb(): Promise<Cart> {
    const url = `${this.API_URLS.CART.GET}`;

    try {
      const cartR = await lastValueFrom(await this.http.get(url));
      const cart = new Cart(cartR);
      return cart;
    } catch (error) {
      return null;
    }
  }

  public add(product: Product, size: string, qty: number): void {
    const id = this.authService.getCurrentUserId();

    let cart = this.$cart.value;
    // if new cart
    if (!cart || (cart && cart.id !== id)) {
      cart = new Cart({
        id,
        products: [],
        createTime: new Date().toDateString(),
        updateTime: new Date().toDateString()
      });
    }

    // check if product with same id and size already exist, then add qtys
    // if added qty is greater then max qty, then set to max qty
    const existP = cart.products.find(
      (p) => p.id === product.id && p.size === size
    );
    if (existP) {
      existP.qty =
        existP.qty + qty > product.qty[size]
          ? product.qty[size]
          : existP.qty + qty;
    } else {
      cart.products.push(
        new InCartProduct({
          id: product.id,
          size,
          qty
        } as InCartProduct)
      );
    }

    this.updateCart(cart);
  }

  public remove(product: Product, size: string): void {
    let cart = this.$cart.value;
    //  remove cart Product
    cart.products = cart.products.filter(
      (p) => !(p.id === product.id && p.size === size)
    );

    this.updateCart(cart);
  }

  public changeSize(
    oldSize: string,
    product: Product,
    size: string,
    qty: number
  ): void {
    let cart = this.$cart.value;

    //  remove cart Product
    cart.products = cart.products.map((p) => {
      if (p.id !== product.id && p.size !== oldSize) {
        p.size = size;
        p.qty = qty;
      }
      return p;
    });

    this.updateCart(cart);
  }

  public updateQty(product: Product, size: string, qty: number): void {
    let cart = this.$cart.value;

    //  remove cart Product
    cart.products = cart.products.map((p) => {
      if (p.id !== product.id && p.size !== size) {
        p.qty = qty;
      }
      return p;
    });

    this.updateCart(cart);
  }

  public clearCart(): void {
    const cart = this.$cart.value;
    if (!cart) return;

    this.$cart.next(null);

    // Clear localstorage
    if (typeof localStorage !== 'undefined') {
      localStorage.removeItem('cart');
    }

    // Delete cart from db siliently
    this.deleteCartInDb(cart);
  }
}

import { HttpClient } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { Product } from '@annuadvent/ngx-core/helpers-ecommerce';
import { BehaviorSubject, Observable, lastValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class InventoryService {
  private $myProducts = new BehaviorSubject<Array<Product>>([]);

  constructor(
    private http: HttpClient,
    @Inject('API_URLS') private API_URLS: any
  ) {}

  public get myProducts(): Observable<Array<Product>> {
    return this.$myProducts.asObservable();
  }

  public async getProducts(force: boolean = false): Promise<Array<Product>> {
    // get from database
    if (!force && this.$myProducts.value?.length) {
      return this.$myProducts.value;
    }

    try {
      const productR: any = await lastValueFrom(
        this.http.get(`${this.API_URLS.PRODUCTS.ALL}`)
      );

      const products = productR.map((p) => new Product(p));

      this.$myProducts.next(products);
      return products;
    } catch (error: any) {
      this.$myProducts.next([]);
      throw error;
    }
  }

  public async getProduct(id: string): Promise<Product> {
    // get from database
    if (!this.$myProducts.value?.length) {
      try {
        const pS = (await this.getProducts(true)) || [];
        return pS.find((p) => p.id === id);
      } catch (error) {
        throw error;
      }
    } else {
      return this.$myProducts.value.find((p) => p.id === id);
    }
  }

  public updateOrAddProduct(product: Product): void {
    let exists = false;

    const updatedList = this.$myProducts.value.map((p) => {
      if (p.id === product.id) {
        exists = true;
        return product;
      } else {
        return p;
      }
    });

    this.$myProducts.next(exists ? updatedList : [product, ...updatedList]);
  }
}

import { Inject, Injectable } from '@angular/core';
import { AppError } from '@annuadvent/ngx-core/app-error';
import {
  Product,
  ProductImageResizeService
} from '@annuadvent/ngx-core/helpers-ecommerce';
import { BehaviorSubject, Observable, lastValueFrom } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { Category } from '@annuadvent/ngx-core/helpers-categories';
import { ImageUpload } from '@annuadvent/ngx-common-ui/image-upload';
import { FireStorageImageService } from '@annuadvent/ngx-tools/fire-storage';
import {
  GlobalConfigParamsEnum,
  GlobalConfigService
} from '@annuadvent/ngx-core/global-config';

@Injectable()
export class ManageProductService {
  private $product = new BehaviorSubject<Product>(null);
  private $error = new BehaviorSubject<AppError>(null);
  private $loading = new BehaviorSubject<boolean>(false);
  private $categories = new BehaviorSubject<Array<Category>>([]);

  constructor(
    @Inject('API_URLS') private API_URLS: any,
    private http: HttpClient,
    private fireImageService: FireStorageImageService,
    private gcService: GlobalConfigService,
    private pirService: ProductImageResizeService
  ) {}

  public get product(): Observable<Product> {
    return this.$product.asObservable();
  }

  public set product(v: Product) {
    this.$product.next(v);
  }

  public get error(): Observable<AppError> {
    return this.$error.asObservable();
  }

  public get loading(): Observable<boolean> {
    return this.$loading.asObservable();
  }

  public get isNewProduct(): boolean {
    return !this.$product.value?.id;
  }

  public get categories(): Observable<Array<Category>> {
    return this.$categories.asObservable();
  }

  public set categories(v: Array<Category>) {
    this.$categories.next(v);
  }

  private generateProductId(): string {
    return Math.floor(new Date().valueOf() * Math.random()).toString();
  }

  private async checkApiUrls(): Promise<void> {
    if (!this.API_URLS) {
      throw new Error(
        `No API_URLS are provided. Provide API_URLS in AppModule`
      );
    }
  }

  private async updateProduct(p: Product): Promise<void> {
    const url = this.isNewProduct
      ? this.API_URLS.PRODUCTS.ADD
      : `${this.API_URLS.PRODUCTS.UPDATE}/${p.id}`;

    try {
      await this.checkApiUrls();

      const result = await lastValueFrom(await this.http.post(url, p));

      this.$product.next(new Product(result));
      this.$loading.next(false);
      !this.$error.value ||
        (!this.$error.value.message && this.$error.next(null));
      return;
    } catch (error) {
      let msg = '';
      if (this.$error.value) {
        msg = this.$error.value.message;
      }
      const err = new AppError(error);
      err.message += msg;
      this.$error.next(err);
      this.$loading.next(false);
      return;
    }
  }

  public async saveBasics(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { id, title, description, brand } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({ ...this.$product.value, id, title, description, brand });

    return await this.updateProduct(p);
  }

  public async saveCategories(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { categories } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({ ...this.$product.value, categories });

    return await this.updateProduct(p);
  }

  public async saveImages(imageUploads: Array<ImageUpload>): Promise<void> {
    if (this.isNewProduct) {
      this.$error.next({
        code: 'PRODUCT_NOT_EXIST',
        message:
          'Product does not exist. First add product basic information, then try again.'
      });

      return;
    }

    this.$error.next(null);
    this.$loading.next(true);

    // Step 1: Save all image files to firebase
    const resizedImgsPromises = imageUploads.map((imageUpload) =>
      this.pirService.resize(imageUpload)
    );
    const resizedImgs = await Promise.all(resizedImgsPromises);

    const fireImgPromises = [];
    imageUploads.forEach((imageUpload, index) => {
      const productRootPath = `${this.gcService.getValue(
        GlobalConfigParamsEnum.productsImagePath
      )}/${this.$product.value.id}`;

      const fullPathSm = `${productRootPath}/sm/${imageUpload.fileName}`;
      const fullPathMd = `${productRootPath}/md/${imageUpload.fileName}`;
      const fullPathLg = `${productRootPath}/lg/${imageUpload.fileName}`;

      fireImgPromises.push(
        Promise.allSettled([
          this.fireImageService.uploadImageByPath(
            fullPathSm,
            resizedImgs[index][0],
            true
          ),
          this.fireImageService.uploadImageByPath(
            fullPathMd,
            resizedImgs[index][1],
            true
          ),
          this.fireImageService.uploadImageByPath(
            fullPathLg,
            imageUpload.data,
            true
          )
        ])
      );
    });

    const results = await Promise.allSettled(fireImgPromises);
    const successFull = [];
    let error: AppError = null;

    results.forEach((result, index) => {
      if (result.status === 'fulfilled') {
        successFull.push(imageUploads[index].fileName);
      } else {
        error = error || new AppError({});
        error.message += `</br><strong> ${imageUploads[index].fileName}</strong>:  ${result.reason}`;
      }
    });
    if (error?.message) this.$error.next(error);

    // Step 2: Update Product

    const { images } = this.$product.value;
    const p: Product = new Product({
      ...this.$product.value,
      images: [...images, ...successFull]
    });

    return await this.updateProduct(p);
  }

  public async deleteProductImage(imgName: string): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const productRootPath = `${this.gcService.getValue(
      GlobalConfigParamsEnum.productsImagePath
    )}/${this.$product.value.id}`;

    const fullPathSm = `${productRootPath}/sm/${imgName}`;
    const fullPathMd = `${productRootPath}/md/${imgName}`;
    const fullPathLg = `${productRootPath}/lg/${imgName}`;

    try {
      const deleteImgPromises = [
        this.fireImageService.deleteImageByPath(fullPathSm),
        this.fireImageService.deleteImageByPath(fullPathMd),
        this.fireImageService.deleteImageByPath(fullPathLg)
      ];

      await Promise.all(deleteImgPromises);

      const images = this.$product.value.images.filter(
        (img) => img !== imgName
      );
      const p: Product = new Product({
        ...this.$product.value,
        images
      });

      return await this.updateProduct(p);
    } catch (error) {
      this.$error.next(new AppError(error));
      this.$loading.next(false);
    }
  }

  public async saveSizes(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { sizes } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({ ...this.$product.value, sizes });

    return await this.updateProduct(p);
  }

  public async savePrice(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { price } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({ ...this.$product.value, price });

    return await this.updateProduct(p);
  }

  public async saveQty(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { qty } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({ ...this.$product.value, qty });

    return await this.updateProduct(p);
  }

  public async saveOffer(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { offer } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({ ...this.$product.value, offer });

    return await this.updateProduct(p);
  }

  public async saveDispatch(product: Product): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    const { minDispatchTime, maxDispatchTime } = product;
    const p: Product = this.isNewProduct
      ? new Product({ ...product, id: this.generateProductId() })
      : new Product({
          ...this.$product.value,
          minDispatchTime,
          maxDispatchTime
        });

    return await this.updateProduct(p);
  }

  public async goLive(): Promise<void> {
    this.$error.next(null);
    this.$loading.next(true);

    if (this.isNewProduct) {
      this.$error.next(
        new AppError(
          'Add product and its all information before getting it GO LIVE'
        )
      );
      this.$loading.next(false);
    }

    if (!this.isProductGoLiveReady()) {
      return;
    }

    const p: Product = new Product({
      ...this.$product.value,
      isLive: true
    });

    return await this.updateProduct(p);
  }

  private isProductGoLiveReady(): boolean {
    const maxLevels = this.gcService.getValue(
      GlobalConfigParamsEnum.categoryMaxLevels
    );

    const error = new AppError({ code: 'GO_LIVE_NOT_READY' });
    let msg = '';
    const p = this.$product.value;
    if (!p.title) msg += 'title missing </br>';
    if (!p.description) msg += 'description missing </br>';
    if (!p.brand) msg += 'brand missing </br>';
    if (p.categories?.length != maxLevels)
      msg += 'One or more Categories missing </br>';
    if (!p.images?.length) msg += 'Product images missing </br>';
    if (!p.sizes?.length) msg += 'Sizes missing </br>';
    if (!p.price) msg += 'price missing </br>';
    if (!p.qty) msg += 'qty missing </br>';
    if (!p.offer) msg += 'offer missing </br>';
    if (!p.minDispatchTime) msg += 'min dispatch time missing </br>';
    if (!p.maxDispatchTime) msg += 'max dispatch time missing </br>';

    if (msg) {
      error.message = msg;
      this.$error.next(error);
      this.$loading.next(false);
      return false;
    } else {
      return true;
    }
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ProductDetailService {

  constructor() { }
}

import { HttpClient } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { Wishlist } from '@annuadvent/ngx-core/helpers-ecommerce';
import { BehaviorSubject, Observable, lastValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class WishlistService {
  private $wishlist = new BehaviorSubject<Wishlist>(null);

  public get wishlist(): Observable<Wishlist> {
    return this.$wishlist.asObservable();
  }

  constructor(
    private http: HttpClient,
    @Inject('API_URLS') private API_URLS: any
  ) {}

  public add(productId: string): void {
    const existingPIds = this.$wishlist.value?.productIds || [];

    if (!existingPIds.includes(productId)) {
      const w = {
        ...this.$wishlist.value,
        productIds: [productId, ...existingPIds]
      };
      this.$wishlist.next(w);
      this.updateOrAddWishlistInDb(w);
    }
  }

  public remove(productId: string): void {
    const existingPIds = this.$wishlist.value?.productIds || [];
    if (existingPIds.includes(productId)) {
      const w = {
        ...this.$wishlist.value,
        productIds: existingPIds.filter((pid) => pid !== productId)
      };

      this.$wishlist.next(w);
      this.updateOrAddWishlistInDb(w);
    }
  }

  public async getWishlist(): Promise<Wishlist> {
    const url = `${this.API_URLS.WISHLIST.GET}`;

    try {
      const wishlistR = await lastValueFrom(await this.http.get(url));
      const wishlist = new Wishlist(wishlistR);
      this.$wishlist.next(wishlist);
      return wishlist;
    } catch (error) {
      return null;
    }
  }

  private async updateOrAddWishlistInDb(wishlist: Wishlist): Promise<void> {
    const url = `${this.API_URLS.WISHLIST.ADD}`;

    try {
      const result = await lastValueFrom(await this.http.post(url, wishlist));
      return;
    } catch (error) {
      return;
    }
  }

  private async deleteWishlistInDb(wishlist: Wishlist): Promise<void> {
    const url = `${this.API_URLS.WISHLIST.DELETE}`;

    try {
      const result = await lastValueFrom(await this.http.post(url, wishlist));
      return;
    } catch (error) {
      return;
    }
  }
}

