import { Component } from '@angular/core';
import { AppImagesService } from '../../services/app-images.service';
import {
  GlobalConfigParamsEnum,
  GlobalConfigService
} from '@annuadvent/ngx-core/global-config';
import { imagesParams } from '../../constants/images-params.constant';
import { AppError } from '@annuadvent/ngx-core/app-error';
import { ImageUpload } from '@annuadvent/ngx-common-ui/image-upload';

@Component({
  selector: 'anu-app-images',
  templateUrl: './app-images.component.html',
  styleUrls: ['./app-images.component.scss']
})
export class AppImagesComponent {
  images: Array<string> = [];
  imagesParams = { ...imagesParams };
  error: AppError = null;
  formImages: {
    bannerImages: [];
    otherImages: [];
  };
  constructor(
    public aiS: AppImagesService,
    private gcService: GlobalConfigService
  ) {
    this.aiS.getImages();

    this.gcService.config.subscribe(() => {
      const { width, height } = this.gcService.getValue(
        GlobalConfigParamsEnum.bannerImageDimensions
      );

      this.imagesParams = {
        ...imagesParams,
        bannerImages: {
          ...imagesParams.bannerImages,
          helpText: `(${width}px X ${height}px) and jpeg format is prefered.`
        }
      };
    });

    this.aiS.images.subscribe((p) => {
      this.images = p;
    });
  }

  private hasDuplicates(imageUploads: Array<ImageUpload>): Array<string> {
    const toUploadImages = imageUploads.map((imgU) => imgU.fileName);
    let duplicates = [];
    this.images.forEach((img) => {
      if (toUploadImages.includes(img)) {
        duplicates.push(img);
      }
    });

    return duplicates;
  }

  public onSave(value: any): void {
    this.error = null;
    const imageUploads: Array<ImageUpload> = [
      ...(value.bannerImages || []),
      ...(value.otherImages || [])
    ];
    const duplicates = this.hasDuplicates(imageUploads);

    if (duplicates.length) {
      this.error = new AppError(
        `Duplicate images - ${duplicates.join('</br>')}`
      );
    } else {
      this.aiS.uploadImages(imageUploads).then((msg) => {
        if (!msg) {
          value.bannerImages = null;
          value.otherImages = null;
        } else {
          this.error = new AppError({ message: msg });
        }
      });
    }
  }

  public onDelete(event: any, img: string) {
    this.aiS.deleteImage(img);
  }
}

import { Component, Inject, Input } from '@angular/core';
import {
  Cart,
  CartProducts,
  InCartProduct,
  Product
} from '@annuadvent/ngx-core/helpers-ecommerce';
import { CartService } from '../../services/cart.service';
import { WishlistService } from '@annuadvent/ngx-ecommerce/wishlist';
import { DEFAULT_PRODUCT_IMAGE_PROVIDER } from '@annuadvent/ngx-ecommerce/product-thumb';
import { AddressService } from '@annuadvent/ngx-ecommerce/address';

@Component({
  selector: 'anu-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent {
  @Input() href: string = '';

  cart: Cart = null;
  cartProducts: CartProducts = null;

  totalMrp: number = 0;
  totalDiscount: number = 0;
  convinienceFee: number = 30;
  shippingFee: number = 60;
  totalToPay: number = 0;

  constructor(
    public cartS: CartService,
    private wS: WishlistService,
    public adS: AddressService,
    @Inject(DEFAULT_PRODUCT_IMAGE_PROVIDER) public defaultProductImage: string
  ) {
    this.cartS.cart.subscribe((cart) => (this.cart = cart));
    this.cartS.cartProducts.subscribe((cartProducts) => {
      this.cartProducts = cartProducts;
      this.calculatePriceSummary();
    });
  }

  private calculatePriceSummary(): void {
    this.totalMrp = 0;
    this.totalDiscount = 0;

    this.cart.products.forEach((cP) => {
      const mrp = this.cartProducts[cP.id]?.price[cP.size] || 0;
      const discount = this.cartProducts[cP.id]?.offer[cP.size] || 0;

      const qty = cP.qty;
      this.totalMrp += qty * mrp;
      this.totalDiscount += (discount * (qty * mrp)) / 100;

      this.totalToPay =
        this.totalMrp +
        this.convinienceFee +
        this.shippingFee -
        this.totalDiscount;
    });
  }
  public onRemoveItem(product: Product, size: string): void {
    this.cartS.remove(product, size);
  }

  public onWishlistItem(product: Product, size: string): void {
    this.wS.add(product.id);
    this.cartS.remove(product, size);
  }

  public onPlaceOrder(): void {
    this.calculatePriceSummary();
  }

  public onQtyChange(qty, cartP: InCartProduct): void {
    setTimeout(() => {
      cartP.qty = qty;
      this.calculatePriceSummary();
    });
  }

  public onSizeChange(size, cartP: InCartProduct): void {
    setTimeout(() => {
      cartP.size = size;
      this.calculatePriceSummary();
    });
  }
}

