export interface FireQueryFilter {
    fieldPath: string;
    operator: StructuredQueryOperatorEnum;
    value: any;
    valueType: StructuredQueryValueType;
};

export interface FireOrderField {
    fieldPath: string;
    fieldType: StructuredQueryValueType;
    isDesc: boolean;
};

export interface FireQuery {
    collectionId: string;
