export class ImageFileInfo {
    name: string = '';
    fullPath: string = '';
    downloadUrl: string = '';

    constructor(imageFIle: any = null) {
        if (imageFIle) {
            this.name = imageFIle.name || '';
            this.fullPath = imageFIle.fullPath || '';
            this.downloadUrl = imageFIle.downloadUrl || '';
        }
    }
}

export interface ImageFileInfoList {
    imageFiles: Array<ImageFileInfo>;
    nextPageToken: string;
}

export interface MetaInfo {
    title?: string;
    description?: string;
    keywords?: string;
    robots?: string;
    'Content-Type'?: string;
    language?: string;
    'revisit-after'?: string;
    author?: string;
    type?: string;
    image?: string;     // social media image url
    url?: string;       // page url, may be canonical url
    card?: string;      // twitter card
    site_name?: string;  // Main site name
    audio?: string;     // audio url
    video?: string;     // video url
    'article:published_time'?: string;      // creation date, ISO format yyyy-mm-dd hh:mm:ss
    'article:author'?: string;              // Author profile url / name
    'article:section'?: string;             // article Category name
    'article:tag'?: string;                 // article keywords or tags
}


export interface ImageInfo {
    src: string;
    alt: string;
    imageData?: any;
};

