import { ImageInfo, MetaInfo } from "./fire-cms.interface";

export interface Article {
    id?: string; // dash separated article id, that can be used as router path.has to be unique, based on article title.
    metaInfo?: MetaInfo; // This info will be used for SEO for the page., title, description, keywords etc.
    image?: ImageInfo; // Thumbnail Image src
    body?: any;
    categories?: Array<string>;
    created?: string;
    updated?: string;
    userId?: string; // User Id from Users
    isLive?: boolean; // true if published and live to web.
    inReview?: boolean; // true if the article is up for review, if true, then isLive must be false
    features?: Array<string>;
}

export interface PageArticles {
    articles: Array<Article>;
    startPage?: any;            // startPage reference
    endPage?: any;              // endPage reference
    previousPage?: any;         // previous page reference if any
    nextPage?: any;             // next page reference if any
};

import { PageArticles } from './fire-articles.interface';
import { ImageInfo, MetaInfo } from './fire-cms.interface';

export interface Category {
    id?: string;
    shortTitle?: string;
    isFeatured?: boolean; // true, if featured category, this can be shown at primary locations on page.
    metaInfo?: MetaInfo; // This info will be used for SEO for the page., title, description, keywords etc.
    image?: ImageInfo; // Thumbnail Image src
    created?: string;
    updated?: string;
    userId?: string; // User Id from Users
    isLive?: boolean;
    inReview?: boolean; // if category is up for review, if this is true, then isLive must be false;
    features?: Array<string>; // any of the values from CategoryFeature, and any custom values.
}

export interface PageCategoryGroup {
    category: Category;
    pageArticles: PageArticles;
};

export interface PageCategories {
    categories: Array<Category>;
    startPage?: any;            // startPage reference
    endPage?: any;              // endPage reference
    previousPage?: any;         // previous page reference if any
    nextPage?: any;             // next page reference if any
};

export class FirebaseConfig {
    app: FireAppConfig;
    ui: any;
    store: FireStoreConfig;
    storage: FireStorageConfig;
}
export interface FireStorageImageSpecs {
    maxKBs: number;
    maxWidth: number;
    maxHeight: number;
    minWidth: number;
    minHeight: number;
};

export interface FireStorageConfig {
    baseStorageUrl: string;     // folder path inside bucket, ex. annu-business/articles
    fireStorageBaseApiUrl: string;  // API full base url
    imageDimensions: FireStorageImageSpecs;
};

export interface FireAppConfig {
    projectId: string;
    appId: string;
    storageBucket: string;
    locationId: string;
    apiKey: string;
    authDomain: string;
    messagingSenderId: string;
    measurementId: string;
}

export interface FireStoreConfig {
    firestoreBaseApiUrl: string;    // Firebase store db url, base api url
}

