  public async getLiveShallowArticlesOfCategories(
    categories: Array<string> | Array<Category>,
    pageSize: number = 0,
    startPage: string | null = null,
    isForwardDir: boolean = true
  ): Promise<Array<PageCategoryGroup>> {
    let pageCategoryGroups: Array<PageCategoryGroup> = [];

    if (categories && categories.length) {
      const fireQuery: FireQuery = {
        collectionId: ARTICLES_COLLECTION_ID,
        where: [
          {
            fieldPath: 'isLive',
            operator: StructuredQueryOperatorEnum.EQUAL,
            value: true,
            valueType: StructuredQueryValueType.booleanValue
          } as FireQueryFilter,
          {
            fieldPath: 'categories',
            operator: StructuredQueryOperatorEnum.ARRAY_CONTAINS,
            value: '',
            valueType: StructuredQueryValueType.stringValue
          } as FireQueryFilter,
        ],
        orderBy: [
          {
            fieldPath: 'updated',
            fieldType: StructuredQueryValueType.stringValue,
            isDesc: true
          } as FireOrderField
        ],
        selectFields: SHALLOW_ARTICLE_FIELDS,
        pageSize,
        isForwardDir,
        startPage: startPage ? [startPage] : null
      };

      try {
        pageCategoryGroups = await Promise.all(
          categories.map(async (cat) => {
            const catId = typeof cat === 'string' ? cat : cat.id;
            fireQuery.where.find(wh => wh.fieldPath === 'categories').value = catId;

            try {
              const catArticles = await this.firestoreHttpService.runQueryByConfig(fireQuery);
              // if a single category, then add pagearticles with previous and next page info else leave that info empty., so that pagination can be enebled for Category articles.
              let pageArticles = await this.buildPageOfArticles(
                catArticles,
                pageSize
              );

              const pageCategoryGroup: PageCategoryGroup = {
                category:
                  typeof cat === 'string' ? ({ id: cat } as Category) : cat,
                pageArticles,
              };

              return pageCategoryGroup;
            } catch (error: any) {
              throw error;
            }
          })
        );
      } catch (error: any) {
        throw error;
      }
    }

    return pageCategoryGroups;
  }

  public async getAllLiveArticlesFromDate(
    fromDateTime: string
  ): Promise<Array<Article>> {

    const fireQuery: FireQuery = {
      collectionId: ARTICLES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'updated',
          operator: StructuredQueryOperatorEnum.GREATER_THAN_OR_EQUAL,
          value: fromDateTime,
          valueType: StructuredQueryValueType.stringValue
        } as FireQueryFilter,
        {
          fieldPath: 'isLive',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: true,
          valueType: StructuredQueryValueType.booleanValue
        } as FireQueryFilter,
      ],
      orderBy: [
        {
          fieldPath: 'updated',
          fieldType: StructuredQueryValueType.stringValue,
          isDesc: true
        } as FireOrderField
      ],
      selectFields: ['categories', 'updated'],
    };

    try {
      const articles = await this.firestoreHttpService.runQueryByConfig(fireQuery);
      return articles;
    } catch (error: any) {
      throw error;
    }
  }

  public async getLiveOnePageShallowArticlesByFeatures(
    features: Array<string>,
    isLive: boolean = true,
    pageSize: number = 0,
    startPage: string | null = null,
    isForwardDir: boolean = true
  ): Promise<PageArticles> {

    const fireQuery: FireQuery = {
      collectionId: ARTICLES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'features',
          operator: StructuredQueryOperatorEnum.ARRAY_CONTAINS_ANY,
          value: features,
          valueType: StructuredQueryValueType.stringValue
        } as FireQueryFilter,
        {
          fieldPath: 'isLive',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: isLive,
          valueType: StructuredQueryValueType.booleanValue
        } as FireQueryFilter,
      ],
      orderBy: [
        {
          fieldPath: 'updated',
          fieldType: StructuredQueryValueType.stringValue,
          isDesc: true
        } as FireOrderField
      ],
      selectFields: SHALLOW_ARTICLE_FIELDS,
      pageSize,
      isForwardDir,
      startPage: startPage ? [startPage] : null
    };

    try {
      const articles = await this.firestoreHttpService.runQueryByConfig(fireQuery);

      return await this.buildPageOfArticles(articles, pageSize);
    } catch (error: any) {
      throw error;
    }
  }

  public async addArticle(article: Article): Promise<Article> {
    const existedArticle = await this.getArticle(article.id).catch(
      (error: any) => {
        if (error.status === 404) {
          return null;
        } else {
          throw error;
        }
      }
    );
    if (existedArticle) throw new Error('Article already Exist.');
    return this.updateArticle(article).catch((error) => {
      throw error;
    });
  }

  public async updateArticle(article: Article): Promise<Article> {
    const fieldsToUpdate = [...UPDATE_ARTICLE_FIELDS, 'isLive'];

    const currentDate = this.utilsSvc.currentDate;
    const pArticle = { ...article, updated: currentDate, isLive: false, inReview: false } as Article;
    pArticle.metaInfo['article:published_time'] = currentDate;
    if (!pArticle.created) pArticle.created = currentDate;
    if (!pArticle.userId) throw new Error('Article userId is required.');


    return this.firestoreHttpService.runQueryToUpdate(ARTICLES_COLLECTION_ID, pArticle, fieldsToUpdate, false)
      .catch((error) => {
        throw error;
      });
  }

  public async setArticleUpForReview(article: Article): Promise<Article> {
    const fieldsToUpdate = ['inReview', 'isLive', 'updated', 'metaInfo'];

    // Any modification to a article, will bring it to unpublished, and not up for review.
    article.isLive = article.inReview === true ? false : article.isLive;
    const currentDate = this.utilsSvc.currentDate;
    article.updated = currentDate;
    article.metaInfo['article:published_time'] = currentDate;

    return this.firestoreHttpService.runQueryToUpdate(ARTICLES_COLLECTION_ID, article, fieldsToUpdate, false)
      .catch((error) => {
        throw error;
      });
  }

  public async setArticleLive(article: Article): Promise<Article> {
    const fieldsToUpdate = ['inReview', 'isLive', 'updated', 'metaInfo'];

    // Any modification to a article, will bring it to unpublished, and not up for review.
    article.inReview = article.isLive === true ? false : article.inReview;
    const currentDate = this.utilsSvc.currentDate;
    article.updated = currentDate;
    article.metaInfo['article:published_time'] = currentDate;

    return this.firestoreHttpService.runQueryToUpdate(ARTICLES_COLLECTION_ID, article, fieldsToUpdate, false)
      .catch((error) => {
        throw error;
      });
  }

  public async deleteArticle(article: Article): Promise<boolean> {
    return this.firestoreHttpService.runQueryToDelete(ARTICLES_COLLECTION_ID, article);
  }
}

import { isPlatformBrowser } from '@angular/common';
import {
  Inject,
  Injectable,
  PLATFORM_ID,
} from '@angular/core';
import {
  getAuth,
  User,
  Auth,
  onAuthStateChanged,
  getIdTokenResult,
} from 'firebase/auth';
import {
  Observable,
  BehaviorSubject,
} from 'rxjs';
import { FireCommonService } from '@annuadvent/ngx-tools/fire-common';
import { FIREBASE_AUTH_ROLES } from '../constants/fire-auth.constants';

@Injectable({
  providedIn: 'root'
})
export class FireAuthService {
  auth: Auth;
  authState: BehaviorSubject<User> = new BehaviorSubject<User>(undefined);

  constructor(
    private fireCommonService: FireCommonService,
    @Inject(PLATFORM_ID) private platformId,
  ) {
    this.auth = this.getFirebaseAuth();
    onAuthStateChanged(this.getFirebaseAuth(), (user: User) => {
      this.authState.next(user)
    });

    this.authState.subscribe(user => {
      if (typeof user !== 'undefined') {
        this.setLoggedInToLocalStorage(user);
      }
    });
  }

  private getFirebaseAuth(): Auth {
    if (this.auth) {
      return this.auth;
    }

    const app = this.fireCommonService.initOrGetFirebaseApp();
    const auth = getAuth(app);

    return auth;
  }

  public getCurrentUser(): User {
    const auth = this.getFirebaseAuth();
    const currentUser = auth.currentUser;
    return currentUser;
  }

  public getCurrentUserId(): string {
    const currentUser = this.getCurrentUser();

    return currentUser && currentUser.uid || '';
  }

  public isLoggedIn(): boolean {
    return !!this.getCurrentUser();
  }

  public authStateChanged(): Observable<User> {
    return this.authState.asObservable();
  }

  public async logout(): Promise<boolean> {
    const auth = this.getFirebaseAuth();
    await auth.signOut();
    this.authState.next(null);

    return true;
  }

  public isLoggedInFromLocalStorage(): User {
    if (isPlatformBrowser(this.platformId)) {
      const currentUser = window.localStorage.getItem('currentUser');

      return currentUser ? JSON.parse(currentUser) as User : null;
    } else {
      return null;
    }
  }

  public setLoggedInToLocalStorage(user: User): void {
    if (isPlatformBrowser(this.platformId)) {
      window.localStorage.setItem('currentUser', '' + user ? JSON.stringify(user) : '');
      if (user) {
        user.getIdToken().then(token => {
          // TODO: if cookie already exists
          if (token) {
            window.document.cookie = 'Authorization=Bearer ' + token;
          }
        })
      } else {
        window.document.cookie = '';
      }
    }
  }

  public async getCurrentUserRoles(): Promise<Array<string>> {
    const currentUser = this.getCurrentUser();
    if (!currentUser) {
      throw new Error('User is not logged in.');
    }

    const idTokenResult = await getIdTokenResult(currentUser);
    const claims = idTokenResult && idTokenResult.claims;
    const claimsArray: Array<string> = [];
    if (claims) {
      Object.keys(FIREBASE_AUTH_ROLES).forEach(key => {
        if (claims[FIREBASE_AUTH_ROLES[key]]) {
          claimsArray.push(FIREBASE_AUTH_ROLES[key]);
        }
      })
    }

    return claimsArray;
  }

  public async currentUserHasRole(role: string): Promise<boolean> {
    const currentUser = this.getCurrentUser();
    if (!currentUser) {
      throw new Error('User is not logged in.');
    }

    const idTokenResult = await getIdTokenResult(currentUser);
    const claims = idTokenResult && idTokenResult.claims;

    if (typeof claims === 'object' && claims[role]) {
      return true;
    }

    return false;
  }

  public async getAccessToken(): Promise<string> {
    const currentUser = this.getCurrentUser();
    if (currentUser) {
      return await currentUser.getIdToken();
    } else {
      return '';
    }
  }
}

import { Injectable } from '@angular/core';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { FireAuthService } from './fire-auth.service';
import { AdditionalUserInfo, User } from 'firebase/auth';
import { FireCommonService } from '@annuadvent/ngx-tools/fire-common';

@Injectable({
  providedIn: 'root'
})
export class FireAuthUiService {
  temp: string = '';

  constructor(private fireCommonService: FireCommonService, private authFireSvc: FireAuthService) { }

  private initOrGetFirebaseCompactApp() {
    return firebase.apps.length ? firebase.apps[0] : firebase.initializeApp(this.fireCommonService.firebaseConfig.app);
  }

  public async startAuthUI(elementId, successCb = null, errorCb = null, uiShownCb = null): Promise<void> {

    const app = this.initOrGetFirebaseCompactApp();
    const auth = firebase.app().auth();

    const firebaseui = await import('firebaseui');
    const ui = firebaseui.auth && (firebaseui.auth.AuthUI.getInstance() || new firebaseui.auth.AuthUI(auth));

    const uiConfig = {
      ...this.fireCommonService.firebaseConfig.ui, callbacks: {
        signInSuccessWithAuthResult: (authResult, redirectUrl) => {
          const user: User = authResult?.user;
          const additionalUserInfo: AdditionalUserInfo = authResult?.additionalUserInfo;

          if (user && additionalUserInfo?.isNewUser) {
            // Check if refresh is required.
            // if the user is new, signed in first time, refresh the token
            user.getIdToken(true)
              .then(() => {
                this.authFireSvc.authState.next(user);
                if (successCb) {
                  successCb(user, redirectUrl);
                }
              });
          } else {
            this.authFireSvc.authState.next(user);
            if (successCb) {
              successCb(user, redirectUrl);
            }
          }
        },
        signInFailure: (error) => {
          // User sign in failure;
          if (errorCb) {
            errorCb(error);
          }
        },
        uiShown: () => {
          // The widget is rendered.
          // Hide the loader.
          if (uiShownCb) {
            uiShownCb();
          }
        }
      }
    };

    setTimeout(() => {
      ui && ui.start(elementId, uiConfig);
    });
  }
}

import { Injectable } from '@angular/core';
import { UtilsService } from '@annuadvent/ngx-core/utils';
import { Category } from '../interfaces/fire-categories.interface';

import {
  FireOrderField,
  FireQuery,
  FireQueryFilter,
  FirestoreHttpService,
  StructuredQueryOperatorEnum,
  StructuredQueryValueType,
} from '@annuadvent/ngx-tools/fire-store';
import {
  CATEGORIES_COLLECTION_ID,
  SHALLOW_CATEGORY_FIELDS,
  UPDATE_CATEGORY_FIELDS,
} from '../constants/fire-categories.constants';

import {
  PageCategories,
  PageCategoryGroup,
} from '../interfaces/fire-categories.interface';
import { FireArticlesHttpService } from './fire-articles-http.service';

@Injectable({
  providedIn: 'root'
})
export class FireCategoriesHttpService {
  constructor(
    private utilsSvc: UtilsService,
    private firestoreHttpService: FirestoreHttpService,
    private fireArticlesHttpService: FireArticlesHttpService,
  ) { }

  private async buildPageOfCategories(
    categories: Array<Category>,
    pageSize: number = 0
  ): Promise<PageCategories> {
    const categoriesCount = categories?.length || 0;
    const pageCategories: PageCategories = {
      categories: categories || [],
      startPage: categoriesCount ? categories[0]?.updated : null,
      endPage:
        categoriesCount && pageSize && categoriesCount === pageSize
          ? categories[categoriesCount - 1]?.updated
          : null,
    };

    return pageCategories;
  }

  public async getCategory(categoryId: string): Promise<Category> {
    try {
      const category: Category = await this.firestoreHttpService.runQueryById(CATEGORIES_COLLECTION_ID, categoryId);
      return category;
    } catch (error: any) {
      throw error;
    }
  }

  public async getUsersCategory(
    userId: string,
    categoryId: string
  ): Promise<Category> {
    if (!userId) throw new Error('Please provide a valid user id.');
    if (!categoryId) throw new Error('Please Provide a valid category id.');

    const fireQuery: FireQuery = {
      collectionId: CATEGORIES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'id',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: categoryId,
          valueType: StructuredQueryValueType.stringValue
        } as FireQueryFilter,
        {
          fieldPath: 'userId',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: userId,
          valueType: StructuredQueryValueType.stringValue
        } as FireQueryFilter
      ]
    };

    try {
      const categories = await this.firestoreHttpService.runQueryByConfig(fireQuery);
      const category = (categories?.length && categories[0]) || null;

      return category;
    } catch (error: any) {
      throw error;
    }
  }

  public async getAllLiveShallowCategories(
    isLive: boolean = true
  ): Promise<Array<Category>> {
    const fireQuery: FireQuery = {
      collectionId: CATEGORIES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'isLive',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: isLive,
          valueType: StructuredQueryValueType.booleanValue
        } as FireQueryFilter,
      ],
      orderBy: [
        {
          fieldPath: 'updated',
          fieldType: StructuredQueryValueType.stringValue,
          isDesc: true
        } as FireOrderField
      ],
      selectFields: SHALLOW_CATEGORY_FIELDS,
    };

    try {
      const categories = await this.firestoreHttpService.runQueryByConfig(fireQuery);
      return categories;
    } catch (error: any) {
      throw error;
    }
  }

  public async getAllLiveCategoriesWithOnePageShallowArticles(
    pageSize: number = 0,
    startPage: string | null = null,
    isForwardDir: boolean = true
  ): Promise<Array<PageCategoryGroup>> {
    const fireQuery: FireQuery = {
      collectionId: CATEGORIES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'isLive',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: true,
          valueType: StructuredQueryValueType.booleanValue
        } as FireQueryFilter,
      ],
      orderBy: [
        {
          fieldPath: 'updated',
          fieldType: StructuredQueryValueType.stringValue,
          isDesc: true
        } as FireOrderField
      ],
      selectFields: SHALLOW_CATEGORY_FIELDS,
    };

    try {
      const categories = await this.firestoreHttpService.runQueryByConfig(fireQuery);
      return await this.fireArticlesHttpService.getLiveShallowArticlesOfCategories(
        categories,
        pageSize,
        startPage,
        isForwardDir
      );
    } catch (error: any) {
      throw error;
    }
  }

  public async getLiveCategoryWithOnePageShallowArticles(
    categoryId: string,
    pageSize: number = 0,
    startPage: string | null = null,
    isForwardDir: boolean = true
  ): Promise<PageCategoryGroup> {
    if (!categoryId) throw new Error('Please provide a valid category id.');

    const fireQuery: FireQuery = {
      collectionId: CATEGORIES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'id',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: categoryId,
          valueType: StructuredQueryValueType.stringValue
        } as FireQueryFilter,
        {
          fieldPath: 'isLive',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: true,
          valueType: StructuredQueryValueType.booleanValue
        } as FireQueryFilter,
      ],
      selectFields: SHALLOW_CATEGORY_FIELDS,
    };

    try {
      const categories = await this.firestoreHttpService.runQueryByConfig(fireQuery);
      const category = (categories?.length && categories[0]) || null;
      if (category) {
        const pageCategoryGroups: Array<PageCategoryGroup> =
          await this.fireArticlesHttpService.getLiveShallowArticlesOfCategories(
            [category],
            pageSize,
            startPage,
            isForwardDir
          );
        return (pageCategoryGroups?.length && pageCategoryGroups[0]) || null;
      }

      return null;
    } catch (error: any) {
      throw error;
    }
  }

