  /**
   * Get all Live categories since the given last updated, by date.
   * @date 2/27/2023 - 6:40:21 PM
   *
   * @public
   * @async
   * @param {string} fromDateTime
   * @returns {Promise<Array<Category>>}
   */
  public async getAllLiveCategoriesFromDate(
    fromDateTime: string
  ): Promise<Array<Category>> {

    const fireQuery: FireQuery = {
      collectionId: CATEGORIES_COLLECTION_ID,
      where: [
        {
          fieldPath: 'isLive',
          operator: StructuredQueryOperatorEnum.EQUAL,
          value: true,
          valueType: StructuredQueryValueType.booleanValue
        } as FireQueryFilter,
        {
          fieldPath: 'updated',
          operator: StructuredQueryOperatorEnum.GREATER_THAN_OR_EQUAL,
          value: fromDateTime,
          valueType: StructuredQueryValueType.stringValue
        } as FireQueryFilter,
      ],
      orderBy: [
        {
          fieldPath: 'updated',
          fieldType: StructuredQueryValueType.stringValue,
          isDesc: true
        } as FireOrderField
      ],
      selectFields: ['updated'],
    };

    try {
      const categories = await this.firestoreHttpService.runQueryByConfig(fireQuery);
      return categories;
    } catch (error: any) {
      throw error;
    }
  }

  public async addCategory(category: Category): Promise<Category> {
    const existedCategory = await this.getCategory(category.id).catch(
      (error: any) => {
        if (error.status === 404) {
          return null;
        } else {
          throw error;
        }
      }
    );
    if (existedCategory) throw new Error('Category already Exist.');

    return this.updateCategory(category)
      .catch((error) => {
        throw error;
      });
  }

  public async updateCategory(category: Category): Promise<Category> {
    const fieldsToUpdate = [...UPDATE_CATEGORY_FIELDS, 'isLive'];

    const currentDate = this.utilsSvc.currentDate;
    const pCategory = { ...category, updated: currentDate, isLive: false, inReview: false };
    pCategory.metaInfo['article:published_time'] = currentDate;
    if (!pCategory.created) pCategory.created = currentDate;
    if (!pCategory.userId) throw new Error('Category userId is required.');

    return this.firestoreHttpService.runQueryToUpdate(CATEGORIES_COLLECTION_ID, pCategory, fieldsToUpdate, false)
      .catch((error) => {
        throw error;
      });
  }

  public async setCategoryUpForReview(category: Category): Promise<Category> {
    const fieldsToUpdate = ['inReview', 'isLive', 'updated', 'metaInfo'];

    // Any modification to a category, will bring it to unpublished, and not up for review.
    category.isLive = category.inReview === true ? false : category.isLive;

    return this.firestoreHttpService.runQueryToUpdate(CATEGORIES_COLLECTION_ID, category, fieldsToUpdate, false)
      .catch((error) => {
        throw error;
      });
  }

  public async setCategoryLive(category: Category): Promise<Category> {
    const fieldsToUpdate = ['inReview', 'isLive', 'updated', 'metaInfo'];

    // Any modification to a category, will bring it to unpublished, and not up for review.

    category.inReview = category.isLive === true ? false : category.inReview;

    return this.firestoreHttpService.runQueryToUpdate(CATEGORIES_COLLECTION_ID, category, fieldsToUpdate, false)
      .catch((error) => {
        throw error;
      });
  }

  public async deleteCategory(category: Category): Promise<boolean> {
    return this.firestoreHttpService.runQueryToDelete(CATEGORIES_COLLECTION_ID, category);
  }
}

import { Injectable } from "@angular/core";
import { FirebaseApp, getApps, initializeApp } from "firebase/app";
import { FirebaseConfig } from "../interfaces/fire-common.interface";

/**
 * FireCommonService defines the firebase config and initializes the firebase application.
 * If firebase application is already initialized, then it returns the initialized application.
 */
@Injectable({
  providedIn: "root",
})
export class FireCommonService {
  private _firebaseConfig: FirebaseConfig;

  constructor() {}

  public get firebaseConfig(): FirebaseConfig {
    return this._firebaseConfig;
  }

  public set firebaseConfig(v: FirebaseConfig) {
    this._firebaseConfig = v;
  }

  public initOrGetFirebaseApp(): FirebaseApp {
    if (!this._firebaseConfig)
      throw new Error(
        "Firebase config is not initialized. First set this.fireCommonService.firebaseConfig with config"
      );
    const firebaseApps = getApps();
    const firebaseApp = firebaseApps.length
      ? firebaseApps[0]
      : initializeApp(this._firebaseConfig.app);

    return firebaseApp;
  }
}

import { Injectable } from '@angular/core';
import { FireCommonService } from '@annuadvent/ngx-tools/fire-common';
import Compressor from 'compressorjs';

import {
  getStorage,
  ref,
  uploadBytes,
  getDownloadURL,
  deleteObject,
  list,
  ListOptions,
  ListResult,
} from 'firebase/storage';
import {
  ImageFileInfo,
  ImageFileInfoList,
} from '../interfaces/fire-storage-image.interface';

@Injectable({
  providedIn: 'root'
})
export class FireStorageImageService {
  public baseStorageUrl: string = '';

  constructor(
    private fireCommonService: FireCommonService,
  ) {
    this.baseStorageUrl = this.fireCommonService.firebaseConfig.storage.baseStorageUrl;
  }

  private buildImageUrl(fullPathWithFileName: string, userId: string): string {
    return (
      this.baseStorageUrl +
      '/' +
      (userId ? `${userId}/${fullPathWithFileName}` : '' + fullPathWithFileName)
    );
  }

  public getUploadImageValidationText(): string {
    const { maxKBs, maxWidth, maxHeight, minWidth, minHeight } = this.fireCommonService.firebaseConfig.storage.imageDimensions;
    return `Allowed width is ${minWidth}px to ${maxWidth}px, height is ${minHeight}px to ${maxHeight}px and size is up to ${maxKBs}KBs`;
  }

  public async validateImage(imageData: any): Promise<string> {
    if (!imageData) return 'Empty Image';

    const { maxKBs } = this.fireCommonService.firebaseConfig.storage.imageDimensions;

    // Type Check
    if (imageData.type.indexOf('image/') < 0) {
      return `Not a valid image file. Please try uploading a valid jpg/gif/png image file.`;
    }

    //Size Check
    if (imageData.size > maxKBs * 1024) {
      return `Image size exceeds the limit of ${maxKBs}KBs`;
    }

    // Dimension Check.
    return await this.validateImageDimension(imageData);
  }

  public async validateImageDimension(imageData: any): Promise<string> {
    const { maxWidth, maxHeight, minWidth, minHeight } =
      this.fireCommonService.firebaseConfig.storage.imageDimensions;

    var reader = new FileReader();

    //Read the contents of Image File.
    reader.readAsDataURL(imageData);

    const promise = new Promise<string>((resolve, reject) => {
      reader.onload = (e) => {
        //Initiate the JavaScript Image object.
        const image = new Image();

        //Set the Base64 string return from FileReader as source.
        image.src = e.target.result as string;
        image.onload = () => {
          // Natural size is the actual image size regardless of rendering.
          // The 'normal' `width`/`height` are for the **rendered** size.
          const width = image.naturalWidth;
          const height = image.naturalHeight;

          if (
            width > maxWidth ||
            minWidth < minWidth ||
            maxHeight > maxHeight ||
            minHeight < minHeight
          ) {
            resolve(
              `Image dimensions are ${width}px X ${height}px and does not meet the requirement. Allowed width = ${minWidth}px to ${maxWidth}px and allowed height = ${minHeight}px to ${maxHeight}px`
            );
          } else {
            resolve('');
          }
        };

        // Reject promise on error
        image.onerror = (error: any) => {
          reject('Error loading Image');
        };
      };
    });

    return promise;
  }

  public async uploadImage(
    fullPathWithFileName: string,
    imageData: any,
    overWrite: boolean = false,
    userId: string = '',
    isBas64: boolean = false
  ): Promise<ImageFileInfo> {
    const fireStorage = getStorage(
      this.fireCommonService.initOrGetFirebaseApp(),
      this.fireCommonService.firebaseConfig.app.storageBucket
    );
    const fileUrl = this.buildImageUrl(fullPathWithFileName, userId);
    const fileRef = ref(fireStorage, fileUrl);

    // If exists
    if (!overWrite) {
      try {
        const isExists = await getDownloadURL(fileRef);
        if (isExists) {
          throw new Error('Image already exists -' + fullPathWithFileName);
        }
      } catch (error: any) {
        if (error.code !== 'storage/object-not-found') {
          throw error;
        }
      }
    }

    try {

      if (isBas64) {
        imageData = await this.compressBase64ToJpeg(imageData);
      }

      const uploadResult = await uploadBytes(fileRef, imageData);
      const downloadUrl = await this.getImageUrl(fullPathWithFileName, userId);
      const imageFileInfo: ImageFileInfo = {
        name: uploadResult.ref.name,
        fullPath: uploadResult.ref.fullPath,
        downloadUrl,
      };

      return imageFileInfo;
    } catch (error: any) {
      throw error;
    }
  }

  public async getImageUrl(
    fullPathWithFileName: string,
    userId: string = ''
  ): Promise<string> {
    const fireStorage = getStorage(
      this.fireCommonService.initOrGetFirebaseApp(),
      this.fireCommonService.firebaseConfig.app.storageBucket
    );
    const fileUrl = this.buildImageUrl(fullPathWithFileName, userId);
    const fileRef = ref(fireStorage, fileUrl);

    try {
      const url = await getDownloadURL(fileRef);

      return url;
    } catch (error: any) {
      throw error;
    }
  }

  public async deleteImage(
    fullPathWithFileName: string,
    userId: string = ''
  ): Promise<string> {
    const fireStorage = getStorage(
      this.fireCommonService.initOrGetFirebaseApp(),
      this.fireCommonService.firebaseConfig.app.storageBucket
    );
    const fileUrl = this.buildImageUrl(fullPathWithFileName, userId);
    const fileRef = ref(fireStorage, fileUrl);

    try {
      await deleteObject(fileRef);
      return fileUrl;
    } catch (error: any) {
      throw error;
    }
  }

  public async getImagesList(    
    userId: string = '',
    pageSize: number = 10,
    nextPageToken: any = null,
  ): Promise<ImageFileInfoList> {
    const fireStorage = getStorage(
      this.fireCommonService.initOrGetFirebaseApp(),
      this.fireCommonService.firebaseConfig.app.storageBucket
    );
    const lookinFolder = this.buildImageUrl('', userId);
    const listRef = ref(fireStorage, lookinFolder);

    try {
      const listOptions: ListOptions = { maxResults: pageSize };
      if (nextPageToken) listOptions.pageToken = nextPageToken;

      const imageList: ListResult = await list(listRef, listOptions);
      const imageFileInfoList: ImageFileInfoList = {
        imageFiles: imageList.items.map((img) => ({
          name: img.name,
          fullPath: img.fullPath,
          downloadUrl: '',
        })),
        nextPageToken: imageList.nextPageToken,
      };

      return imageFileInfoList;
    } catch (error: any) {
      throw error;
    }
  }

  public async imageExists(
    fullPathWithFileName: string,
    userId: string = ''
  ): Promise<boolean> {
    const fireStorage = getStorage(
      this.fireCommonService.initOrGetFirebaseApp(),
      this.fireCommonService.firebaseConfig.app.storageBucket
    );
    const fileUrl = this.buildImageUrl(fullPathWithFileName, userId);
    const fileRef = ref(fireStorage, fileUrl);

    try {
      const url = await getDownloadURL(fileRef);

      return !!url;
    } catch (error: any) {
      if (error.code !== 'storage/object-not-found') {
        throw error;
      } else {
        return false;
      }
    }
  }

  public async compressBase64ToJpeg(base64Image: string): Promise<Blob> {
    const { maxWidth } =
      this.fireCommonService.firebaseConfig.storage.imageDimensions;

    return new Promise((resolve, reject) => {
      // Convert base64 to blob
      const blob = this.dataURItoBlob(base64Image, 'image/jpeg');
      const compressOptions: Compressor.Options = {
        strict: true,
        checkOrientation: true,
        width: maxWidth, // Set the maximum width of the compressed image (optional)
        height: maxWidth / 2,
        maxWidth: maxWidth, // Set the maximum width of the compressed image (optional)
        maxHeight: maxWidth / 2,
        resize: 'cover',
        convertTypes: 'image/jpg',
        quality: 0.7, // Set the image quality (optional)

        success: (compressedBlob: Blob) => {
          // Handle the compressed image blob
          resolve(compressedBlob);
        },
        error: (error: any) => {
          reject(`Image compression error:${error.message}`);
        },
      }
      // Create an image compressor instance
      new Compressor(blob, compressOptions);
    });
  }

  public dataURItoBlob(dataURI: string, type: string): Blob {
    const byteString = atob(dataURI);
    const arrayBuffer = new ArrayBuffer(byteString.length);
    const uint8Array = new Uint8Array(arrayBuffer);

    for (let i = 0; i < byteString.length; i++) {
      uint8Array[i] = byteString.charCodeAt(i);
    }

    return new Blob([uint8Array], { type });
  }
}

