import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { FireCommonService } from '@annuadvent/ngx-tools/fire-common';
import { FirestoreParserService } from './firestore-parser.service';
import { FireQuery } from '../interfaces/fire-query.interface';
import { FirestoreQueryService } from './firestore-query.service';
import { BIN_COLLECTION_POSTFIX, RUN_QUERY_KEYWORD } from '../constants/firestore.constants';

@Injectable({
  providedIn: 'root'
})
export class FirestoreHttpService {

  constructor(
    private fireCommonService: FireCommonService,
    private http: HttpClient,
    private firestoreParser: FirestoreParserService,
    private firestoreQueryService: FirestoreQueryService,
  ) { }

  public async runQueryById(collectionId: string, id: string): Promise<any> {
    const firestoreApiUrl = this.fireCommonService.firebaseConfig?.store?.firestoreBaseApiUrl;

    return new Promise((resolve, reject) => {
      if (!collectionId || !id) {
        reject('Please provide a valid collection id and document id.');
      } else {
        const url = `${firestoreApiUrl}/${collectionId}/${id}`;
        const httpSubscription = this.http.get(url).subscribe({
          next: (queryResult: any) => {
            const parsedQueryResult: any = this.firestoreParser.parse(queryResult);
            httpSubscription.unsubscribe();
            resolve(parsedQueryResult);
          },
          error: reject,
        });
      }
    });
  }

  public async runQueryByConfig(
    fireQuery: FireQuery
  ): Promise<Array<any>> {
    const firestoreApiUrl = this.fireCommonService.firebaseConfig?.store?.firestoreBaseApiUrl;

    return new Promise((resolve, reject) => {
      const url = `${firestoreApiUrl}${RUN_QUERY_KEYWORD}`;

      const httpSubscription = this.http
        .post(
          url,
          this.firestoreQueryService.fireToStructuredQuery(fireQuery)
        )
        .subscribe({
          next: (queryResult: any) => {
            const parsedQueryResult: Array<any> = this.firestoreParser.parse(
              queryResult
            );
            httpSubscription.unsubscribe();
            resolve(parsedQueryResult);
          },
          error: (err) => reject({ config: fireQuery, error: err }),
        });
    });
  }

  public async runQueryToUpdate(
    collectionId: string,
    collectionDoc: any,
    fieldsToUpdate: Array<string>,
    isBin: boolean = false
  ): Promise<any> {
    const firestoreApiUrl = this.fireCommonService.firebaseConfig?.store?.firestoreBaseApiUrl;

    if (!collectionDoc || !collectionDoc.id) throw new Error('Please provide a valid collection document');

    // If update to Bin collection, then postfix collectionId with '-bin'
    if (isBin === true) collectionId = `${collectionId}${BIN_COLLECTION_POSTFIX}`;

    const pCollectionDoc = { ...collectionDoc };
    delete pCollectionDoc.id;

    return new Promise((resolve, reject) => {
      const url = `${firestoreApiUrl}/${collectionId}/${collectionDoc.id}`;
      const body = this.firestoreParser.buildFirebaseFields(
        pCollectionDoc,
        fieldsToUpdate
      );
      const params =
        this.firestoreParser.buildQueryParamsToUpdate(fieldsToUpdate);

      const httpSubscription = this.http
        .patch(url, body, { params })
        .subscribe({
          next: (queryResult: any) => {
            const updatedCollectionDoc: any = this.firestoreParser.parse(
              queryResult
            );
            httpSubscription.unsubscribe();
            resolve(updatedCollectionDoc);
          },
          error: reject,
        });
    });
  }

  public async runQueryToDelete(collectionId: string, collectionDoc: any): Promise<boolean> {
    const firestoreApiUrl = this.fireCommonService.firebaseConfig?.store?.firestoreBaseApiUrl;

    if (!collectionDoc || !collectionDoc.id)
      throw new Error('Please provide a valid collectionDoc.');

    return new Promise((resolve, reject) => {
      // Copy to collectionDocs bin, then delete from collectionDocs db.
      this.runQueryToUpdate(collectionId, collectionDoc, null, true)
        .then(() => {
          const url = `${firestoreApiUrl}/${collectionId}/${collectionDoc.id}`;
          // delete from collectionDocs db.
          const httpSubscription = this.http.delete(url).subscribe({
            next: (res: any) => {
              httpSubscription.unsubscribe();
              resolve(true);
            },
            error: reject,
          });
        })
        .catch(reject);
    });
  }

}

import { HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { FirebaseDocument } from '../interfaces/fire-doc.interface';
import {
  StructuredQueryArrayValue,
  StructuredQueryMapValue,
  StructuredQueryValue,
} from '../interfaces/structured-query.interface';
import {
  StructuredQueryValueType
} from '../enums/structured-query.enums';


@Injectable({
  providedIn: 'root'
})
export class FirestoreParserService {

  constructor() { }

  private getFireStoreProp(value: any): string | undefined {
    const props: Array<string> = [
      'arrayValue',
      'bytesValue',
      'booleanValue',
      'doubleValue',
      'geoPointValue',
      'integerValue',
      'mapValue',
      'nullValue',
      'referenceValue',
      'stringValue',
      'timestampValue',
      'fields',
      'document'
    ];
    return Object.keys(value).find(k => props.includes(k));
  }

  public parse(value: any) {
    // Remove readTime
    if (value instanceof Array) {
      if (value.length === 1 && typeof value[0] === 'object' && Object.keys(value[0]).includes('readTime') && Object.keys(value[0]).length === 1) {
        value.splice(0, 1);
      }
    }

    if (typeof value === 'object') {
      const objKeys = Object.keys(value);
      if (objKeys.includes('readTime')) delete value.readTime;
    }


    const prop = this.getFireStoreProp(value);

    if (prop === 'doubleValue' || prop === 'integerValue') {
      value = Number(value[prop]);
    }
    else if (prop === 'arrayValue') {
      value = (value[prop] && value[prop].values || []).map((v: any) => this.parse(v))
    }
    else if (prop === 'mapValue') {
      value = this.parse(value[prop] && value[prop].fields || {})
    }
    else if (prop === 'geoPointValue') {
      value = { latitude: 0, longitude: 0, ...value[prop] }
    }
    // extract and add id to the document.
    else if (prop === 'fields') {
      const nextValue = value[prop];
      const docName: string = value.name;
      if (docName && nextValue) {
        const docId = docName.substring(docName.lastIndexOf('/') + 1);
        value[prop].id = docId;
      }
      value = this.parse(value[prop])
    }
    else if (prop === 'document') {
      const nextValue = value[prop];
      const docName: string = nextValue.name;
      if (docName && nextValue.fields) {
        const docId = docName.substring(docName.lastIndexOf('/') + 1);
        value[prop].fields.id = docId;
      }
      value = this.parse(value[prop])
    }
    else if (prop) {
      value = value[prop]
    }
    else if (typeof value === 'object') {
      Object.keys(value).forEach(k => value[k] = this.parse(value[k]))
    }

    return value;
  }

  public buildFirebaseFields(doc: Object, fieldsToUpdate: Array<string>): FirebaseDocument {
    if (!doc) throw new Error('Please provide a valid document object containing one or more fields.');

    const firebaseDoc: FirebaseDocument = {
      fields: {}
    };

    if (fieldsToUpdate && fieldsToUpdate.length) {
      const missingPropsOnDoc = [];
      fieldsToUpdate.forEach(fieldName => {
        const fieldValue = doc[fieldName];
        // ensures fieldsToUpdate field has its property on doc, else remove it from fields to update.
        if (typeof fieldValue !== 'undefined') {
          firebaseDoc.fields[fieldName] = this.getFieldStructuredValue(fieldValue);
        } else {
          missingPropsOnDoc.push(fieldName);
        }
      });

      // remove missing props (undefined) from the fieldsToUpdate. As firestore does not update missing properties.
      fieldsToUpdate = fieldsToUpdate.filter(fieldName => !missingPropsOnDoc.includes(fieldName));
    } else {
      firebaseDoc.fields = this.getFieldStructuredValue(doc).mapValue.fields;
    }

    return firebaseDoc;
  }

  public getFieldStructuredValue(value: any): StructuredQueryValue {
    let valueType: StructuredQueryValueType;
    if (typeof value === 'number') {
      valueType = StructuredQueryValueType.doubleValue;
    } else if (typeof value === 'string') {
      valueType = StructuredQueryValueType.stringValue;
    } else if (typeof value === 'boolean') {
      valueType = StructuredQueryValueType.booleanValue;
    } else if (value instanceof Array) {
      valueType = StructuredQueryValueType.arrayValue;
      const arrayValue: StructuredQueryArrayValue = { values: [] };
      value.forEach(itemValue => {
        arrayValue.values.push(this.getFieldStructuredValue(itemValue));
      });
      value = arrayValue;
    } else if (typeof value === 'object') {
      valueType = StructuredQueryValueType.mapValue;
      const mapValue: StructuredQueryMapValue = { fields: {} };
      Object.keys(value).forEach(key => mapValue.fields[key] = this.getFieldStructuredValue(value[key]));
      value = mapValue;
    }
    const structuredQueryValue: StructuredQueryValue = { [valueType]: value };

    return structuredQueryValue;
  }

  public buildQueryParamsToUpdate(fieldsToUpdate: Array<string>): HttpParams {
    let params: HttpParams = new HttpParams();

    fieldsToUpdate?.forEach(fieldName => {
      params = params.append('updateMask.fieldPaths', fieldName);
    });

    return params;
  }
}

import { Injectable } from '@angular/core';
import {
  StructuredQuery,
  StructuredQueryCollectionSelector,
  StructuredQueryCursor,
  StructuredQueryFieldFilter,
  StructuredQueryFieldReference,
  StructuredQueryFilter,
  StructuredQueryOrder,
  StructuredQuerySelectProjection,
  StructuredQueryType,
} from '../interfaces/structured-query.interface';
import {
  FireOrderField,
  FireQuery,
  FireQueryFilter,
} from '../interfaces/fire-query.interface';
import {
  StructuredQueryDirectionEnum,
  StructuredQueryOperatorEnum,
  StructuredQueryValueType,
} from '../enums/structured-query.enums';
import { FireCommonService } from '@annuadvent/ngx-tools/fire-common';


@Injectable({
  providedIn: 'root'
})
export class FirestoreQueryService {

  constructor(private fireCommonService: FireCommonService) { }

  private getSelectFields(selectFields: Array<string>): StructuredQuerySelectProjection {
    return {
      fields: selectFields.map(field => ({ fieldPath: field }))
    } as StructuredQuerySelectProjection;
  }

  private getOrderBy(orderBy: Array<FireOrderField>): Array<StructuredQueryOrder> {
    return orderBy.map(orderByField => {
      let { fieldPath, isDesc } = orderByField;

      if (orderByField.fieldPath === 'id') {
        fieldPath = '__name__';
      }

      return {
        field: { fieldPath: fieldPath } as StructuredQueryFieldReference,
        direction: isDesc === true ? StructuredQueryDirectionEnum.DESCENDING : StructuredQueryDirectionEnum.ASCENDING
      } as StructuredQueryOrder
    });
  }

  private getStartPage(startPage: Array<any>, orderBy: Array<FireOrderField>): StructuredQueryCursor {
    const values = orderBy.map((orderField, index) => ({ [orderField.fieldType]: startPage[index] || 'null' }))
    return {
      values,
      before: false
    } as StructuredQueryCursor;
  }

  private getWhere(where: Array<FireQueryFilter>, collectionId: string): StructuredQueryFilter {
    const firestoreApiUrl = this.fireCommonService.firebaseConfig?.store?.firestoreBaseApiUrl;

    const filters = where.map(fireFilter => {
      let { fieldPath, operator, value, valueType } = fireFilter;

      if (value === null || typeof value === 'undefined') {
        return null;
      }

      /*
      * If Where has a filter for id, that means it has to be filtered on document's __name__ reference fieldpath.
      * Also __name__ has to be in orderBy. So add it to orderBy separately.
     */
      if (fieldPath === 'id') {
        const collectionUrl = firestoreApiUrl.substring(
          firestoreApiUrl.indexOf('/projects/') + 1
        );

        fieldPath = '__name__';
        value = value instanceof Array ?
          value.map(val => `${collectionUrl}/${collectionId}/${val}`) :
          `${collectionUrl}/${collectionId}/${value}`;
        valueType = StructuredQueryValueType.referenceValue;
      }

      return {
        fieldFilter: {
          field: { fieldPath: fieldPath },
          op: operator,
          value: value instanceof Array ?
            { arrayValue: { values: value.map(v => ({ [valueType]: v })) } } :
            { [valueType]: value },
        } as StructuredQueryFieldFilter
      }
    });

    const validFilters = filters.filter(ft => ft !== null);

    if (validFilters && validFilters.length) {
      return {
        compositeFilter: {
          filters,
          op: StructuredQueryOperatorEnum.AND
        }
      } as StructuredQueryFilter;
    } else {
      return null;
    }
  }

  public fireToStructuredQuery(fireQuery: FireQuery): StructuredQuery {
    if (!fireQuery) throw new Error('Invalid Fire Query.');
    let { collectionId, selectFields, orderBy, where, isForwardDir, startPage, pageSize } = fireQuery;

    let squery: StructuredQueryType = {
      from: [{ collectionId, allDescendants: false } as StructuredQueryCollectionSelector]
    };

    /*
    * If id is in the where filters, then add id(__name__) to orderBy, its mandatory.
    */
    if (where && where.length && where.find(filter => filter.fieldPath === 'id')) {
      orderBy = orderBy ? [...orderBy] : [];
      // if not, id orderBy already exist, then add it to order By
      if (!orderBy.find(ord => ord.fieldPath === 'id')) {
        orderBy.push(
          {
            fieldPath: 'id',
            fieldType: StructuredQueryValueType.referenceValue,
            isDesc: true,
          } as FireOrderField
        )
      }
    }


    if (selectFields && selectFields.length) {
      squery.select = this.getSelectFields(selectFields);
    }

    if (orderBy && orderBy.length) {
      squery.orderBy = this.getOrderBy(orderBy);
    }

    if (startPage && startPage.length && orderBy && orderBy.length && pageSize > 0) {
      if (isForwardDir === false) {
        squery.endAt = this.getStartPage(startPage, orderBy);
      } else {
        squery.startAt = this.getStartPage(startPage, orderBy);
      }
    }

    if (pageSize > 0) {
      squery.limit = pageSize;
    }

    if (where && where.length) {
      const validWhere = this.getWhere(where, collectionId);
      if (validWhere) {
        squery.where = validWhere;
      }
    }

    return { structuredQuery: squery } as StructuredQuery;
  }

}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { ImageFileInfo, ImageFileInfoList } from '../interfaces/fire-storage-image.interface';
import { FireStorageImageService } from './fire-storage-image.service';
import { FireAuthService } from '@annuadvent/ngx-tools/fire-auth';
import { PreviewOnImages } from '../interfaces/image-browser.interface';

@Injectable({
  providedIn: 'root'
})
export class ImageBrowserService {
  private imageFiles$ = new BehaviorSubject<Array<ImageFileInfo>>([]);
  private selected$ = new BehaviorSubject<ImageFileInfo>(null);
  private previewOnImages$ = new BehaviorSubject<PreviewOnImages>({});
  private _pageSize: number = 10;

  userId: string = '';
  nextPageToken: any = null;
  uploadValidationText: string;

  constructor(
    private fireStorageImageService: FireStorageImageService,
    private fireAuthService: FireAuthService,
    ) { 
    // If logged in user changed or loged off, then list should be cleared.
    this.fireAuthService.authStateChanged().subscribe((user: any) => {
      if(user?.id && user.id !== this.userId) {
        this.clear();
        this.userId = user.id;
      } else if(!user?.id) {
        this.clear();
        this.userId = null;
      }
    })
  }

  private clear(): void {
    this.imageFiles$.next([]);
    this.previewOnImages$.next({});
    this.selected$.next(null);
  }

  public get pageSize() : number {
    return this._pageSize;
  }
  
  public set pageSize(v : number) {
    this._pageSize = v;
  }
  
  public get imageFiles(): Observable<Array<ImageFileInfo>> {
    return this.imageFiles$.asObservable();
  }

  public get selected(): Observable<ImageFileInfo> {
    return this.selected$.asObservable();
  }

  public get previewOnImages(): Observable<PreviewOnImages> {
    return this.previewOnImages$.asObservable();
  }

  public async refreshList(): Promise<Array<ImageFileInfo>> {
    this.userId = this.fireAuthService.getCurrentUserId();
    if(!this.userId) {
      throw new Error('User is not authenticated or logged in.');
    }

    const imageFileInfoList: ImageFileInfoList = await this.fireStorageImageService.getImagesList(this.userId, this.pageSize, this.nextPageToken)
      .catch(err => {
        throw err;
      });

    this.nextPageToken = imageFileInfoList?.nextPageToken || null;
    this.uploadValidationText = this.fireStorageImageService.getUploadImageValidationText();
    this.previewOnImages$.next({});
    this.selected$.next(null);
    this.imageFiles$.next(imageFileInfoList?.imageFiles || []);

    return imageFileInfoList?.imageFiles || [];
  }

  public async loadMore(): Promise<Array<ImageFileInfo>> {
    this.userId = this.fireAuthService.getCurrentUserId();
    if(!this.userId) {
      throw new Error('User is not authenticated or logged in.');
    }
    const imageFileInfoList: ImageFileInfoList = await this.fireStorageImageService.getImagesList(this.userId, this.pageSize, this.nextPageToken)
      .catch(err => {
        throw err;
      });

    this.nextPageToken = imageFileInfoList?.nextPageToken || null;
    this.imageFiles$.next([
      ...this.imageFiles$.value,
      ...(imageFileInfoList?.imageFiles || [])
    ]);

    return imageFileInfoList?.imageFiles || [];
  }

  public async delete(imageFile: ImageFileInfo): Promise<ImageFileInfo> {  
    this.userId = this.fireAuthService.getCurrentUserId();
    if(!this.userId) {
      throw new Error('User is not authenticated or logged in.');
    }
      
    const deletedFileFullPath = await this.fireStorageImageService.deleteImage(imageFile?.name, this.userId)
      .catch(err => {
        throw err;
      });

    // Successful delete, remove it from the existing list too;

    this.imageFiles$.next(
      this.imageFiles$.value.filter(iFile => iFile.fullPath !== deletedFileFullPath)
    );

    return imageFile;
  }

  public async upload(imagePathWithFileName: string, imageData: any): Promise<ImageFileInfo> {
    this.userId = this.fireAuthService.getCurrentUserId();
    if(!this.userId) {
      throw new Error('User is not authenticated or logged in.');
    }

    const errorMessage = await this.fireStorageImageService.validateImage(imageData)
    .catch(err => err);
    if(errorMessage) {
      throw new Error(errorMessage);
    }

    const uploadedImageFile: ImageFileInfo = await this.fireStorageImageService.uploadImage(
      imagePathWithFileName,
      imageData,
      false,
      this.userId,
      false,
    )
      .catch(err => {
        throw err;
      });

    // Add uploaded file to 
    this.imageFiles$.next([
      uploadedImageFile,
      ...this.imageFiles$.value
    ]);

    return uploadedImageFile;
  }

  public selectImageFile(imageFile: ImageFileInfo): void {
    this.selected$.next(imageFile);
  }

  public previewImageFile(imageFullFileName: string, previewOn: boolean = true): void {
    this.previewOnImages$.next({
      ...this.previewOnImages$.value,
      [imageFullFileName]: previewOn
    });
  }

}

import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, RouterStateSnapshot, Router, UrlTree } from '@angular/router';
import { FireAuthService } from '../services/fire-auth.service';
import { FIREBASE_AUTH_ROLES } from '../constants/fire-auth.constants';


@Injectable({
  providedIn: 'root'
})
export class IsLoggedInGuard {

  constructor(private fireAuthService: FireAuthService,
    private router: Router) { }

  async canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Promise<boolean | UrlTree> {
    return this.checkUserLoggedIn(route, state);
  }

  async canActivateChild(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Promise<boolean | UrlTree> {
    return this.checkUserLoggedIn(route, state);
  }

  public async checkUserLoggedIn(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Promise<boolean | UrlTree> {
    const redirectUrl = route?.data['redirectUrl'] || '/login';
    const urlTree = this.router.parseUrl(`${redirectUrl}?returnUrl=${state.url}`);

    return new Promise((resolve, reject) => {
      if (this.fireAuthService.isLoggedIn()) {
        resolve(true);
      } else {
        this.fireAuthService.authStateChanged().subscribe(user => {
          if (typeof user === 'undefined') {
            // Do nothing. As user is unknown (Firebase auth has not checked for the current user)
          } else if (user) {
            resolve(true);
          } else {
            resolve(urlTree);
          }
        })
      }
    })
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { OpenaiConfiguration } from '../interfaces/openai-http.interface';
import { OPENAI_CONFIGURATION_DEFAULT } from '../constants/openai-http.constants';
import { OpenaiConfig } from '../interfaces/openai.interface';

@Injectable({
  providedIn: 'root'
})
export class OpenaiConfigService {
  private config$ = new BehaviorSubject<OpenaiConfiguration>(OPENAI_CONFIGURATION_DEFAULT);

  constructor() { }


  public get config(): Observable<OpenaiConfiguration> {
    return this.config$.asObservable();
  }


  public get configValue(): OpenaiConfiguration {
    return this.config$.value;
  }

  public initOpenai(apiKey: string, organization: string) {
    this.config$.next(this.mergeKeyConfigWithBaseConfig(this.config$.value, { apiKey, organization }));
  }

  public mergeKeyConfigWithBaseConfig(baseConfig: OpenaiConfiguration, keyConfig: OpenaiConfig): OpenaiConfiguration {
    const { apiKey, organization } = keyConfig;

    return {
      ...baseConfig,
      apiKey: {
        ...baseConfig.apiKey,
        value: apiKey
      },
      headers: {
        ...baseConfig.headers,
        value: {
          ...baseConfig.headers.value,
          'OpenAI-Organization': {
            ...baseConfig.headers.value['OpenAI-Organization'],
            value: organization,
          },
        }
      }
    }
  }

  public getKeyConfig(config: OpenaiConfiguration): OpenaiConfig {
    const openaiConfig: OpenaiConfig = {
      apiKey: config?.apiKey?.value,
      organization: config?.headers?.value['OpenAI-Organization'].value,
    };

    return openaiConfig;
  }

  public updateConfig(config: OpenaiConfiguration) {
    this.config$.next(config);
  }
}

