import { Component, EventEmitter, Input, Output } from '@angular/core';
import { OpenaiService } from '../../services/openai.service';
import { OpenaiImageSize } from '../../interfaces/openai.interface';
import { FireStorageImageService, ImageFileInfo } from '@annuadvent/ngx-tools/fire-storage';
import { FireAuthService } from '@annuadvent/ngx-tools/fire-auth';

@Component({
  selector: 'anu-openai-image-form',
  templateUrl: './openai-image-form.component.html',
  styleUrls: ['./openai-image-form.component.scss'],
})
export class OpenaiImageFormComponent {
  @Input() imagePromptText: string = '';
  @Input() imageFileName: string = '';
  @Output() imageGenerated = new EventEmitter<string>();
  @Output() fileUploaded = new EventEmitter<ImageFileInfo>();

  generatedBase64Image: string = '';
  savedImageUrl: string = '';
  loading: boolean = false;
  errorMsg: string = '';

  constructor(
    private openaiService: OpenaiService,
    private fireStorageImageService: FireStorageImageService,
    private fireAuthService: FireAuthService,
  ) { }

  public async generateImage(): Promise<string> {
    if (!this.imagePromptText.trim()) return '';
    this.startLoading();
    try {
      [this.generatedBase64Image] = await this.openaiService.getImagetResponse(
        this.imagePromptText,
        1,
        OpenaiImageSize._1024x1024
      );
      this.stopLoading();
      this.imageGenerated.emit(this.generatedBase64Image);
      return this.generatedBase64Image;
    } catch (err: any) {
      this.generatedBase64Image = '';
      this.stopLoading(err?.message);
      this.imageGenerated.emit('');
      return '';
    }
  }

  public async uploadImage(): Promise<void> {
    this.startLoading();
    if (!this.generatedBase64Image || !this.imageFileName) {
      this.stopLoading('Empty Image or not a valid file name');
      return;
    }
    

    const userId = this.fireAuthService.getCurrentUserId();
    if(!userId) {
      this.stopLoading('User is not authenticated or logged in.');
      return;
    }
    
    try {
      const imageFileInfo: ImageFileInfo =
        await this.fireStorageImageService.uploadImage(
          this.imageFileName,
          this.generatedBase64Image,
          true,
          userId,
          true
        );
        this.generatedBase64Image = '';
      this.savedImageUrl = `getImage?imageId=${imageFileInfo.fullPath}`;
      this.fileUploaded.emit(imageFileInfo);

      this.stopLoading();
      return;
    } catch (err) {
      this.stopLoading(err.message);
      return;
    }
  }

  private startLoading(): void {
    this.loading = true;
    this.errorMsg = '';
  }

  private stopLoading(err: string = ''): void {
    this.loading = false;
    this.errorMsg = err;
  }

}

import { Component, EventEmitter, Input, OnChanges, OnDestroy, OnInit, Output, SimpleChanges } from '@angular/core';
import { Queue } from '../../classes/queue.class';
import { QueueItem } from '../../classes/queue-item.class';
import { QueueStatusEnum } from '../../enums/queue.enums';
import { Subscription } from 'rxjs';
/**
 * Visualizes the Queue running status, with counts and status for inprogress, paused, notstarted, failed and completeed.
 */
@Component({
  selector: 'anu-queue-status',
  templateUrl: './queue-status.component.html',
  styleUrls: ['./queue-status.component.scss']
})
export class QueueStatusComponent implements OnInit, OnChanges, OnDestroy {
  @Input() queue: Queue = null;
  @Output() startClick = new EventEmitter<void>();

  qItems: Array<QueueItem> = [];
  qStatus: QueueStatusEnum = QueueStatusEnum.notstarted;
  totalCount: number = 0;
  completedCount: number = 0;
  notstartedCount: number = 0;
  failedCount: number = 0;
  inprogressCount: number = 0;
  queueSubscription: Subscription = null;
  statusSubscription: Subscription = null;
  timerSubscription: Subscription = null;
  timeEllapsed: number = 0;

  constructor() {

  }

  ngOnInit(): void {
    this.subscribeQueue();
  }

  ngOnChanges(changes: SimpleChanges): void {
    this.subscribeQueue();
  }

  ngOnDestroy(): void {
    // Unsubscribe if already subscribed
    this.queueSubscription && this.queueSubscription.unsubscribe();
    this.statusSubscription && this.statusSubscription.unsubscribe();
    this.timerSubscription && this.timerSubscription.unsubscribe();
  }

  private subscribeQueue(): void {
    if (!this.queue) return;
    // Unsubscribe if already subscribed
    this.queueSubscription && this.queueSubscription.unsubscribe();
    this.statusSubscription && this.statusSubscription.unsubscribe();
    this.timerSubscription && this.timerSubscription.unsubscribe();

    // Subscribe queue items change
    this.queue?.queue?.subscribe((qItems: Array<QueueItem>) => {
      this.qItems = qItems;
      this.refreshQueueCounters();
    });

    // Subscribe queue status change
    this.queue?.status?.subscribe((status: QueueStatusEnum) => this.qStatus = status);

    // Subscribe timer
    this.queue?.timer?.subscribe((timeStarted: number) => this.timeEllapsed = timeStarted ? Math.round((Date.now() - timeStarted) / 1000) : 0);
  }

  private refreshQueueCounters(): void {
    this.totalCount = this.qItems?.length || 0;
    this.completedCount = 0;
    this.notstartedCount = 0;
    this.failedCount = 0;
    this.inprogressCount = 0;

    // Calculate counters by cheching all items
    this.qItems.map(qItem => {
      switch (qItem.status) {
        case QueueStatusEnum.notstarted:
          this.notstartedCount++;
          break;
        case QueueStatusEnum.inprogress:
          this.inprogressCount++;
          break;
        case QueueStatusEnum.completed:
          this.completedCount++;
          break;
        case QueueStatusEnum.failed:
          this.failedCount++;
          break;
      }
    });
  }

  public startClicked(event: any): void {
    switch (this.qStatus) {
      case QueueStatusEnum.notstarted:
        this.startClick.emit(event);
        break;
      case QueueStatusEnum.inprogress:
        this.queue?.pause();
        break;
      case QueueStatusEnum.paused:
        this.queue?.resume();
        break;
      default:
        console.log('Queue is complted or stopped already Or may be not initialized yet.');
    }
  }
}

