$queue_badge_size: 2em;

:host {

    .queue {
        &_badge {
            border-radius: 50%;
            width: $queue_badge_size;
            height: $queue_badge_size;
            line-height: $queue_badge_size;
            background-color: rgb(162, 0, 215, 0.5);

            &.notstarted {
                background-color: rgba(124, 124, 124, 0.651);
            }

            &.completed {
                background-color: rgba(25, 121, 0, 0.548);
            }

            &.inprogress {
                background-color: rgba(0, 238, 12, 0.5);
            }

            &.failed {
                background-color: rgba(235, 0, 0, 0.5);
            }

            &.paused {
                background-color: rgba(14, 187, 255, 0.534);
            }

            &.timer {
                border-radius: 0.5rem;
                width: $queue_badge_size * 2;
            }
        }
    }
}

